        String option1 = etOption1 != null ? etOption1.getText().toString().trim() : "";
        String option2 = etOption2 != null ? etOption2.getText().toString().trim() : "";
        String option3 = etOption3 != null ? etOption3.getText().toString().trim() : "";
        String option4 = etOption4 != null ? etOption4.getText().toString().trim() : "";

        if (question.isEmpty() || option1.isEmpty() || option2.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập câu hỏi và ít nhất 2 đáp án", Toast.LENGTH_SHORT).show();
            return;
        }

        int correctAnswer = 0;
        if (rgCorrectAnswer != null && rgCorrectAnswer.getCheckedRadioButtonId() != -1) {
            RadioButton selectedButton = findViewById(rgCorrectAnswer.getCheckedRadioButtonId());
            // Logic to determine correct answer index
        }

        // Add question to list (implementation would involve adapter)
        Toast.makeText(this, "Câu hỏi đã được thêm", Toast.LENGTH_SHORT).show();
        clearQuestionForm();
    }

    private void saveQuiz() {
        // Implementation similar to saveLesson
        Toast.makeText(this, "Quiz đã được lưu", Toast.LENGTH_SHORT).show();
    }

    private void previewQuiz() {
        Intent intent = new Intent(this, QuizActivity.class);
        intent.putExtra("mode", "preview");
        startActivity(intent);
    }

    private void addWord() {
        String word = etWord != null ? etWord.getText().toString().trim() : "";
        String meaning = etMeaning != null ? etMeaning.getText().toString().trim() : "";
        String example = etExample != null ? etExample.getText().toString().trim() : "";

        if (word.isEmpty() || meaning.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập từ và nghĩa", Toast.LENGTH_SHORT).show();
            return;
        }

        // Add word to vocabulary list
        Toast.makeText(this, "Từ vựng đã được thêm", Toast.LENGTH_SHORT).show();
        clearVocabularyForm();
    }

    private void saveVocabularyList() {
        Toast.makeText(this, "Danh sách từ vựng đã được lưu", Toast.LENGTH_SHORT).show();
    }

    private void publishContent() {
        android.app.AlertDialog.Builder builder = new android.app.AlertDialog.Builder(this);
        builder.setTitle("Xuất bản nội dung")
                .setMessage("Bạn có chắc chắn muốn xuất bản nội dung này? Học viên sẽ có thể truy cập.")
                .setPositiveButton("Xuất bản", (dialog, which) -> {
                    Toast.makeText(this, "Nội dung đã được xuất bản!", Toast.LENGTH_SHORT).show();
                })
                .setNegativeButton("Hủy", null)
                .show();
    }

    private void clearLessonForm() {
        if (etLessonTitle != null) etLessonTitle.setText("");
        if (etLessonDescription != null) etLessonDescription.setText("");
        if (etLessonContent != null) etLessonContent.setText("");
    }

    private void clearQuestionForm() {
        if (etQuestion != null) etQuestion.setText("");
        if (etOption1 != null) etOption1.setText("");
        if (etOption2 != null) etOption2.setText("");
        if (etOption3 != null) etOption3.setText("");
        if (etOption4 != null) etOption4.setText("");
        if (rgCorrectAnswer != null) rgCorrectAnswer.clearCheck();
    }

    private void clearVocabularyForm() {
        if (etWord != null) etWord.setText("");
        if (etMeaning != null) etMeaning.setText("");
        if (etExample != null) etExample.setText("");
        if (etPronunciation != null) etPronunciation.setText("");
    }

    private void loadExistingContent() {
        updateContentCount();
    }

    private void updateContentCount() {
        if (teacherId != null) {
            db.collection("content")
                    .whereEqualTo("teacherId", teacherId)
                    .get()
                    .addOnSuccessListener(queryDocumentSnapshots -> {
                        int count = queryDocumentSnapshots.size();
                        if (tvContentCount != null) {
                            tvContentCount.setText("Đã tạo: " + count + " nội dung");
                        }
                    });
        }
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        if (item.getItemId() == android.R.id.home) {
            onBackPressed();
            return true;
        }
        return super.onOptionsItemSelected(item);
    }

    @Override
    public void onBackPressed() {
        super.onBackPressed();
        Intent intent = new Intent(this, TeacherDashboardActivity.class);
        intent.addFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);
        startActivity(intent);
        finish();
    }
}

package com.vantinh.tienganh;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.cardview.widget.CardView;
import androidx.recyclerview.widget.RecyclerView;
import java.text.SimpleDateFormat;
import java.util.List;
import java.util.Locale;

public class CourseAdapter extends RecyclerView.Adapter<CourseAdapter.CourseViewHolder> {

    private List<Course> courseList;
    private OnCourseClickListener listener;

    public interface OnCourseClickListener {
        void onCourseClick(Course course);
        void onManageLessons(Course course);
        void onEditCourse(Course course);
    }

    public CourseAdapter(List<Course> courseList, OnCourseClickListener listener) {
        this.courseList = courseList;
        this.listener = listener;
    }

    // Method to update course list for filtering
    public void updateCourseList(List<Course> newCourseList) {
        this.courseList = newCourseList;
        notifyDataSetChanged();
    }

    @NonNull
    @Override
    public CourseViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_course, parent, false);
        return new CourseViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull CourseViewHolder holder, int position) {
        Course course = courseList.get(position);
        holder.bind(course);
    }

    @Override
    public int getItemCount() {
        return courseList.size();
    }

    class CourseViewHolder extends RecyclerView.ViewHolder {
        private CardView cardCourse;
        private ImageView ivCourseImage;
        private TextView tvCourseTitle;
        private TextView tvCourseDescription;
        private TextView tvCourseLevel;
        private TextView tvCourseCategory;
        private TextView tvCourseDuration;
        private TextView tvEnrolledStudents;
        private TextView tvCourseRating;
        private TextView tvCreatedDate;
        private Button btnManageLessons;
        private Button btnEditCourse;

        public CourseViewHolder(@NonNull View itemView) {
            super(itemView);
            cardCourse = itemView.findViewById(R.id.card_course);
            ivCourseImage = itemView.findViewById(R.id.iv_course_image);
            tvCourseTitle = itemView.findViewById(R.id.tv_course_title);
            tvCourseDescription = itemView.findViewById(R.id.tv_course_description);
            tvCourseLevel = itemView.findViewById(R.id.tv_course_level);
            tvCourseCategory = itemView.findViewById(R.id.tv_course_category);
            tvCourseDuration = itemView.findViewById(R.id.tv_course_duration);
            tvEnrolledStudents = itemView.findViewById(R.id.tv_enrolled_students);
            tvCourseRating = itemView.findViewById(R.id.tv_course_rating);
            tvCreatedDate = itemView.findViewById(R.id.tv_created_date);
            btnManageLessons = itemView.findViewById(R.id.btn_manage_lessons);
            btnEditCourse = itemView.findViewById(R.id.btn_edit_course);
        }

        public void bind(Course course) {
            tvCourseTitle.setText(course.getTitle());
            tvCourseDescription.setText(course.getDescription());
            tvCourseLevel.setText(course.getLevel());
            tvCourseCategory.setText(course.getCategory());
            tvCourseDuration.setText(course.getDuration() + " giờ");
            tvEnrolledStudents.setText(course.getEnrolledStudents() + " học viên");
            tvCourseRating.setText(String.format(Locale.getDefault(), "%.1f★", course.getRating()));

            if (course.getCreatedAt() != null) {
                SimpleDateFormat sdf = new SimpleDateFormat("dd/MM/yyyy", Locale.getDefault());
                tvCreatedDate.setText("Tạo: " + sdf.format(course.getCreatedAt()));
            }

            // Set course image (placeholder for now)
            ivCourseImage.setImageResource(R.drawable.ic_course_default);

            // Set up click listeners
            cardCourse.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onCourseClick(course);
                }
            });

            btnManageLessons.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onManageLessons(course);
                }
            });

            btnEditCourse.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onEditCourse(course);
                }
            });

            // Add animation
            cardCourse.setOnTouchListener((v, event) -> {
                switch (event.getAction()) {
                    case android.view.MotionEvent.ACTION_DOWN:
                        v.animate().scaleX(0.95f).scaleY(0.95f).setDuration(100).start();
                        break;
                    case android.view.MotionEvent.ACTION_UP:
                    case android.view.MotionEvent.ACTION_CANCEL:
                        v.animate().scaleX(1.0f).scaleY(1.0f).setDuration(100).start();
                        break;
                }
                return false;
            });
        }
    }
}

package com.vantinh.tienganh;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.cardview.widget.CardView;
import androidx.recyclerview.widget.RecyclerView;
import com.google.firebase.firestore.FirebaseFirestore;
import java.text.SimpleDateFormat;
import java.util.List;
import java.util.Locale;

public class CourseAdapter extends RecyclerView.Adapter<CourseAdapter.CourseViewHolder> {

    private List<Course> courseList;
    private OnCourseClickListener listener;
    private FirebaseFirestore db;

    public interface OnCourseClickListener {
        void onCourseClick(Course course);
        void onManageLessons(Course course);
        void onEditCourse(Course course);
    }

    public CourseAdapter(List<Course> courseList, OnCourseClickListener listener) {
        this.courseList = courseList;
        this.listener = listener;
        this.db = FirebaseFirestore.getInstance();
    }

    // Method to update course list for filtering
    public void updateCourseList(List<Course> newCourseList) {
        this.courseList = newCourseList;
        notifyDataSetChanged();
    }

    @NonNull
    @Override
    public CourseViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_course, parent, false);
        return new CourseViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull CourseViewHolder holder, int position) {
        Course course = courseList.get(position);
        holder.bind(course);
    }

    @Override
    public int getItemCount() {
        return courseList.size();
    }

    class CourseViewHolder extends RecyclerView.ViewHolder {
        private CardView cardCourse;
        private ImageView ivCourseImage;
        private TextView tvCourseTitle;
        private TextView tvCourseDescription;
        private TextView tvCourseLevel;
        private TextView tvCourseCategory;
        private TextView tvCourseDuration;
        private TextView tvEnrolledStudents;
        private TextView tvCourseRating;
        private TextView tvCreatedDate;
        private Button btnManageLessons;
        private Button btnEditCourse;

        public CourseViewHolder(@NonNull View itemView) {
            super(itemView);
            cardCourse = itemView.findViewById(R.id.card_course);
            ivCourseImage = itemView.findViewById(R.id.iv_course_image);
            tvCourseTitle = itemView.findViewById(R.id.tv_course_title);
            tvCourseDescription = itemView.findViewById(R.id.tv_course_description);
            tvCourseLevel = itemView.findViewById(R.id.tv_course_level);
            tvCourseCategory = itemView.findViewById(R.id.tv_course_category);
            tvCourseDuration = itemView.findViewById(R.id.tv_course_duration);
            tvEnrolledStudents = itemView.findViewById(R.id.tv_enrolled_students);
            tvCourseRating = itemView.findViewById(R.id.tv_course_rating);
            tvCreatedDate = itemView.findViewById(R.id.tv_created_date);
            btnManageLessons = itemView.findViewById(R.id.btn_manage_lessons);
            btnEditCourse = itemView.findViewById(R.id.btn_edit_course);
        }

        public void bind(Course course) {
            tvCourseTitle.setText(course.getTitle());
            tvCourseDescription.setText(course.getDescription());
            tvCourseLevel.setText(course.getLevel());
            tvCourseCategory.setText(course.getCategory());
            tvCourseDuration.setText(course.getDuration() + " giờ");

            // Load số học viên thực tế từ courseRequests với status "approved"
            loadEnrolledStudentsCount(course.getId(), tvEnrolledStudents);

            tvCourseRating.setText(String.format(Locale.getDefault(), "%.1f★", course.getRating()));

            if (course.getCreatedAt() != null) {
                SimpleDateFormat sdf = new SimpleDateFormat("dd/MM/yyyy", Locale.getDefault());
                tvCreatedDate.setText(" " + sdf.format(course.getCreatedAt()));
            }

            // Set course image (placeholder for now)
            ivCourseImage.setImageResource(R.drawable.ic_course_default);

            // Set up click listeners
            cardCourse.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onCourseClick(course);
                }
            });

            btnManageLessons.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onManageLessons(course);
                }
            });

            btnEditCourse.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onEditCourse(course);
                }
            });

            // Add animation
            cardCourse.setOnTouchListener((v, event) -> {
                switch (event.getAction()) {
                    case android.view.MotionEvent.ACTION_DOWN:
                        v.animate().scaleX(0.95f).scaleY(0.95f).setDuration(100).start();
                        break;
                    case android.view.MotionEvent.ACTION_UP:
                    case android.view.MotionEvent.ACTION_CANCEL:
                        v.animate().scaleX(1.0f).scaleY(1.0f).setDuration(100).start();
                        break;
                }
                return false;
            });
        }

        private void loadEnrolledStudentsCount(String courseId, TextView tvEnrolledStudents) {
            // Hiển thị loading state
            tvEnrolledStudents.setText(" ⏳...");

            // Query courseRequests với status "approved" cho courseId này
            db.collection("courseRequests")
                    .whereEqualTo("courseId", courseId)
                    .whereEqualTo("status", "approved")
                    .get()
                    .addOnSuccessListener(queryDocumentSnapshots -> {
                        // Đếm unique studentId để tránh trùng lặp
                        java.util.Set<String> uniqueStudentIds = new java.util.HashSet<>();

                        for (com.google.firebase.firestore.QueryDocumentSnapshot doc : queryDocumentSnapshots) {
                            String studentId = doc.getString("studentId");
                            String studentName = doc.getString("studentName");
                            String courseName = doc.getString("courseName");

                            android.util.Log.d("CourseAdapter", "Found approved request - Student: " + studentName + 
                                  ", StudentId: " + studentId + ", Course: " + courseName + ", CourseId: " + courseId);

                            if (studentId != null && !studentId.isEmpty()) {
                                uniqueStudentIds.add(studentId);
                            }
                        }

                        int enrolledCount = uniqueStudentIds.size();
                        android.util.Log.d("CourseAdapter", "Course " + courseId + " has " + enrolledCount + " unique enrolled students");

                        // Cập nhật UI với số học viên thực tế
                        tvEnrolledStudents.setText(" " + enrolledCount + " học viên");
                    })
                    .addOnFailureListener(e -> {
                        android.util.Log.e("CourseAdapter", "Error loading enrolled students for course: " + courseId, e);
                        // Hiển thị fallback nếu có lỗi
                        tvEnrolledStudents.setText(" 0 học viên");
                    });
        }
    }
}

package com.vantinh.tienganh;

import android.app.AlertDialog;
import android.content.Intent;
import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.view.Menu;
import android.view.MenuItem;
import android.view.View;
import android.widget.ArrayAdapter;
import android.widget.LinearLayout;
import android.widget.Spinner;
import android.widget.TextView;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.google.android.material.button.MaterialButton;
import com.google.android.material.floatingactionbutton.FloatingActionButton;
import com.google.android.material.textfield.TextInputEditText;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.QueryDocumentSnapshot;
import java.util.ArrayList;
import java.util.List;

public class CourseManagementActivity extends AppCompatActivity {

    private RecyclerView rvCourses;
    private FloatingActionButton fabAddCourse;
    private LinearLayout tvNoCourses;
    private Toolbar toolbar;
    private TextInputEditText etSearch;
    private MaterialButton btnFilter;
    private TextView tvTotalCourses, tvTotalStudents, tvAvgRating, tvCourseCount;

    private FirebaseAuth mAuth;
    private FirebaseFirestore db;
    private List<Course> courseList;
    private List<Course> filteredCourseList;
    private CourseAdapter courseAdapter;

    // Filter variables
    private String selectedCategory = "Tất cả";
    private String selectedLevel = "Tất cả";
    private String searchQuery = "";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_course_management);

        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();
        courseList = new ArrayList<>();
        filteredCourseList = new ArrayList<>();

        initViews();
        setupToolbar();
        setupRecyclerView();
        setupSearchAndFilter();
        setupClickListeners();
        loadCourses();
        loadStatistics();
    }

    private void initViews() {
        toolbar = findViewById(R.id.toolbar);
        rvCourses = findViewById(R.id.rv_courses);
        fabAddCourse = findViewById(R.id.fab_add_course);
        tvNoCourses = findViewById(R.id.tv_no_courses);
        etSearch = findViewById(R.id.et_search);
        btnFilter = findViewById(R.id.btn_filter);
        tvTotalCourses = findViewById(R.id.tv_total_courses);
        tvTotalStudents = findViewById(R.id.tv_total_students);
        tvAvgRating = findViewById(R.id.tv_avg_rating);
        tvCourseCount = findViewById(R.id.tv_course_count);
    }

    private void setupToolbar() {
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Quản lý khóa học");
            getSupportActionBar().setDisplayHomeAsUpEnabled(true);
        }
    }

    private void setupRecyclerView() {
        courseAdapter = new CourseAdapter(courseList, new CourseAdapter.OnCourseClickListener() {
            @Override
            public void onCourseClick(Course course) {
                // Xem chi tiết khóa học
                Intent intent = new Intent(CourseManagementActivity.this, EditCourseActivity.class);
                intent.putExtra("courseId", course.getId());
                startActivity(intent);
            }

            @Override
            public void onManageLessons(Course course) {
                // Chuyển đến quản lý bài học
                Intent intent = new Intent(CourseManagementActivity.this, LessonManagementActivity.class);
                intent.putExtra("courseId", course.getId());
                intent.putExtra("courseTitle", course.getTitle());
                intent.putExtra("courseCategory", course.getCategory());
                startActivity(intent);
            }

            @Override
            public void onEditCourse(Course course) {
                // Chỉnh sửa khóa học
                Intent intent = new Intent(CourseManagementActivity.this, EditCourseActivity.class);
                intent.putExtra("courseId", course.getId());
                startActivity(intent);
            }
        });
        rvCourses.setLayoutManager(new LinearLayoutManager(this));
        rvCourses.setAdapter(courseAdapter);
    }

    private void setupSearchAndFilter() {
        // Setup search functionality
        if (etSearch != null) {
            etSearch.addTextChangedListener(new TextWatcher() {
                @Override
                public void beforeTextChanged(CharSequence s, int start, int count, int after) {}

                @Override
                public void onTextChanged(CharSequence s, int start, int before, int count) {
                    searchQuery = s.toString().trim();
                    filterCourses();
                }

                @Override
                public void afterTextChanged(Editable s) {}
            });
        }

        // Setup filter button
        if (btnFilter != null) {
            btnFilter.setOnClickListener(v -> showFilterDialog());
        }
    }

    private void showFilterDialog() {
        AlertDialog.Builder builder = new AlertDialog.Builder(this);
        View dialogView = getLayoutInflater().inflate(R.layout.dialog_course_filter, null);

        Spinner spinnerCategory = dialogView.findViewById(R.id.spinner_category);
        Spinner spinnerLevel = dialogView.findViewById(R.id.spinner_level);
        MaterialButton btnApply = dialogView.findViewById(R.id.btn_apply_filter);
        MaterialButton btnReset = dialogView.findViewById(R.id.btn_reset_filter);

        // Setup category spinner
        String[] categories = {"Tất cả", "Ngữ pháp", "Từ vựng", "Phát âm", "Nghe", "Nói", "Đọc", "Viết"};
        ArrayAdapter<String> categoryAdapter = new ArrayAdapter<>(this, android.R.layout.simple_spinner_item, categories);
        categoryAdapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinnerCategory.setAdapter(categoryAdapter);

        // Setup level spinner
        String[] levels = {"Tất cả", "Beginner", "Intermediate", "Advanced"};
        ArrayAdapter<String> levelAdapter = new ArrayAdapter<>(this, android.R.layout.simple_spinner_item, levels);
        levelAdapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinnerLevel.setAdapter(levelAdapter);

        // Set current selections
        spinnerCategory.setSelection(getCategoryPosition(selectedCategory));
        spinnerLevel.setSelection(getLevelPosition(selectedLevel));

        AlertDialog dialog = builder.setView(dialogView).create();

        btnApply.setOnClickListener(v -> {
            selectedCategory = spinnerCategory.getSelectedItem().toString();
            selectedLevel = spinnerLevel.getSelectedItem().toString();
            filterCourses();
            updateFilterButtonText();
            dialog.dismiss();
            Toast.makeText(this, "Đã áp dụng bộ lọc", Toast.LENGTH_SHORT).show();
        });

        btnReset.setOnClickListener(v -> {
            selectedCategory = "Tất cả";
            selectedLevel = "Tất cả";
            spinnerCategory.setSelection(0);
            spinnerLevel.setSelection(0);
            filterCourses();
            updateFilterButtonText();
            dialog.dismiss();
            Toast.makeText(this, "Đã reset bộ lọc", Toast.LENGTH_SHORT).show();
        });

        dialog.show();
    }

    private int getCategoryPosition(String category) {
        String[] categories = {"Tất cả", "Ngữ pháp", "Từ vựng", "Phát âm", "Nghe", "Nói", "Đọc", "Viết"};
        for (int i = 0; i < categories.length; i++) {
            if (categories[i].equals(category)) {
                return i;
            }
        }
        return 0;
    }

    private int getLevelPosition(String level) {
        String[] levels = {"Tất cả", "Beginner", "Intermediate", "Advanced"};
        for (int i = 0; i < levels.length; i++) {
            if (levels[i].equals(level)) {
                return i;
            }
        }
        return 0;
    }

    private void updateFilterButtonText() {
        boolean hasFilters = !selectedCategory.equals("Tất cả") || !selectedLevel.equals("Tất cả");
        if (hasFilters) {
            btnFilter.setText(" Lọc (" + getActiveFilterCount() + ")");
        } else {
            btnFilter.setText(" Lọc");
        }
    }

    private int getActiveFilterCount() {
        int count = 0;
        if (!selectedCategory.equals("Tất cả")) count++;
        if (!selectedLevel.equals("Tất cả")) count++;
        return count;
    }

    private void filterCourses() {
        filteredCourseList.clear();

        for (Course course : courseList) {
            boolean matchesSearch = searchQuery.isEmpty() ||
                course.getTitle().toLowerCase().contains(searchQuery.toLowerCase()) ||
                course.getDescription().toLowerCase().contains(searchQuery.toLowerCase());

            boolean matchesCategory = selectedCategory.equals("Tất cả") ||
                course.getCategory().equals(selectedCategory);

            boolean matchesLevel = selectedLevel.equals("Tất cả") ||
                course.getLevel().equals(selectedLevel);

            if (matchesSearch && matchesCategory && matchesLevel) {
                filteredCourseList.add(course);
            }
        }

        // Update adapter with filtered list
        courseAdapter.updateCourseList(filteredCourseList);
        updateUI();
    }

    private void updateUI() {
        if (filteredCourseList.isEmpty()) {
            tvNoCourses.setVisibility(View.VISIBLE);
            rvCourses.setVisibility(View.GONE);
        } else {
            tvNoCourses.setVisibility(View.GONE);
            rvCourses.setVisibility(View.VISIBLE);
        }

        // Update course count
        if (tvCourseCount != null) {
            String countText = filteredCourseList.size() + " khóa học";
            if (!searchQuery.isEmpty() || !selectedCategory.equals("Tất cả") || !selectedLevel.equals("Tất cả")) {
                countText += " (đã lọc)";
            }
            tvCourseCount.setText(countText);
        }
    }

    private void loadStatistics() {
        if (mAuth.getCurrentUser() == null) return;

        String currentUserId = mAuth.getCurrentUser().getUid();

        // Load total students count
        db.collection("enrollments")
            .whereEqualTo("teacherId", currentUserId)
            .get()
            .addOnSuccessListener(queryDocumentSnapshots -> {
                if (tvTotalStudents != null) {
                    tvTotalStudents.setText(String.valueOf(queryDocumentSnapshots.size()));
                }
            });

        // Set average rating (placeholder)
        if (tvAvgRating != null) {
            tvAvgRating.setText("4.8");
        }
    }

    private void loadCourses() {
        // Check if user is authenticated
        if (mAuth.getCurrentUser() == null) {
            Toast.makeText(this, "Bạn cần đăng nhập để xem khóa học", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        String currentUserId = mAuth.getCurrentUser().getUid();

        db.collection("courses")
            .whereEqualTo("teacherId", currentUserId)
            .get()
            .addOnCompleteListener(task -> {
                if (task.isSuccessful()) {
                    courseList.clear();

                    for (QueryDocumentSnapshot document : task.getResult()) {
                        Course course = document.toObject(Course.class);
                        course.setId(document.getId());
                        courseList.add(course);
                    }

                    // Update total courses count
                    if (tvTotalCourses != null) {
                        tvTotalCourses.setText(String.valueOf(courseList.size()));
                    }

                    // Apply current filters
                    filterCourses();

                } else {
                    android.util.Log.e("CourseManagement", "Error loading courses", task.getException());
                    Toast.makeText(this, "Lỗi khi tải khóa học: " + task.getException().getMessage(), Toast.LENGTH_SHORT).show();
                }
            });
    }

    private void setupClickListeners() {
        fabAddCourse.setOnClickListener(v -> {
            Intent intent = new Intent(this, CreateCourseActivity.class);
            startActivity(intent);
        });

        // Add click listener for create first course button in empty state
        MaterialButton btnCreateFirstCourse = findViewById(R.id.btn_create_first_course);
        if (btnCreateFirstCourse != null) {
            btnCreateFirstCourse.setOnClickListener(v -> {
                Intent intent = new Intent(this, CreateCourseActivity.class);
                startActivity(intent);
            });
        }
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.course_management_menu, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        int itemId = item.getItemId();

        if (itemId == android.R.id.home) {
            finish();
            return true;
        } else if (itemId == R.id.action_refresh) {
            loadCourses();
            return true;
        } else if (itemId == R.id.action_search) {
            // Focus on search input
            if (etSearch != null) {
                etSearch.requestFocus();
            }
            return true;
        }

        return super.onOptionsItemSelected(item);
    }

    @Override
    protected void onResume() {
        super.onResume();
        loadCourses(); // Refresh data when returning to this activity
    }
}

package com.vantinh.tienganh;

import android.app.AlertDialog;
import android.content.Intent;
import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.view.Menu;
import android.view.MenuItem;
import android.view.View;
import android.widget.ArrayAdapter;
import android.widget.LinearLayout;
import android.widget.Spinner;
import android.widget.TextView;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.google.android.material.button.MaterialButton;
import com.google.android.material.floatingactionbutton.FloatingActionButton;
import com.google.android.material.textfield.TextInputEditText;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.QueryDocumentSnapshot;
import java.util.ArrayList;
import java.util.List;

public class CourseManagementActivity extends AppCompatActivity {

    private RecyclerView rvCourses;
    private FloatingActionButton fabAddCourse;
    private LinearLayout tvNoCourses;
    private Toolbar toolbar;
    private TextInputEditText etSearch;
    private MaterialButton btnFilter;
    private TextView tvTotalCourses, tvTotalStudents, tvAvgRating, tvCourseCount;

    private FirebaseAuth mAuth;
    private FirebaseFirestore db;
    private List<Course> courseList;
    private List<Course> filteredCourseList;
    private CourseAdapter courseAdapter;

    // Filter variables
    private String selectedCategory = "Tất cả";
    private String selectedLevel = "Tất cả";
    private String searchQuery = "";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_course_management);

        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();
        courseList = new ArrayList<>();
        filteredCourseList = new ArrayList<>();

        initViews();
        setupToolbar();
        setupRecyclerView();
        setupSearchAndFilter();
        setupClickListeners();
        loadCourses();
        loadStatistics();
    }

    private void initViews() {
        toolbar = findViewById(R.id.toolbar);
        rvCourses = findViewById(R.id.rv_courses);
        fabAddCourse = findViewById(R.id.fab_add_course);
        tvNoCourses = findViewById(R.id.tv_no_courses);
        etSearch = findViewById(R.id.et_search);
        btnFilter = findViewById(R.id.btn_filter);
        tvTotalCourses = findViewById(R.id.tv_total_courses);
        tvTotalStudents = findViewById(R.id.tv_total_students);
        tvAvgRating = findViewById(R.id.tv_avg_rating);
        tvCourseCount = findViewById(R.id.tv_course_count);
    }

    private void setupToolbar() {
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Quản lý khóa học");
            getSupportActionBar().setDisplayHomeAsUpEnabled(true);
        }
    }

    private void setupRecyclerView() {
        courseAdapter = new CourseAdapter(courseList, new CourseAdapter.OnCourseClickListener() {
            @Override
            public void onCourseClick(Course course) {
                // Xem chi tiết khóa học
                Intent intent = new Intent(CourseManagementActivity.this, EditCourseActivity.class);
                intent.putExtra("courseId", course.getId());
                startActivity(intent);
            }

            @Override
            public void onManageLessons(Course course) {
                // Chuyển đến quản lý bài học
                Intent intent = new Intent(CourseManagementActivity.this, LessonManagementActivity.class);
                intent.putExtra("courseId", course.getId());
                intent.putExtra("courseTitle", course.getTitle());
                intent.putExtra("courseCategory", course.getCategory());
                startActivity(intent);
            }

            @Override
            public void onEditCourse(Course course) {
                // Chỉnh sửa khóa học
                Intent intent = new Intent(CourseManagementActivity.this, EditCourseActivity.class);
                intent.putExtra("courseId", course.getId());
                startActivity(intent);
            }
        });
        rvCourses.setLayoutManager(new LinearLayoutManager(this));
        rvCourses.setAdapter(courseAdapter);
    }

    private void setupSearchAndFilter() {
        // Setup search functionality
        if (etSearch != null) {
            etSearch.addTextChangedListener(new TextWatcher() {
                @Override
                public void beforeTextChanged(CharSequence s, int start, int count, int after) {}

                @Override
                public void onTextChanged(CharSequence s, int start, int before, int count) {
                    searchQuery = s.toString().trim();
                    filterCourses();
                }

                @Override
                public void afterTextChanged(Editable s) {}
            });
        }

        // Setup filter button
        if (btnFilter != null) {
            btnFilter.setOnClickListener(v -> showFilterDialog());
        }
    }

    private void showFilterDialog() {
        AlertDialog.Builder builder = new AlertDialog.Builder(this);
        View dialogView = getLayoutInflater().inflate(R.layout.dialog_course_filter, null);

        Spinner spinnerCategory = dialogView.findViewById(R.id.spinner_category);
        Spinner spinnerLevel = dialogView.findViewById(R.id.spinner_level);
        MaterialButton btnApply = dialogView.findViewById(R.id.btn_apply_filter);
        MaterialButton btnReset = dialogView.findViewById(R.id.btn_reset_filter);

        // Setup category spinner
        String[] categories = {"Tất cả", "Ngữ pháp", "Từ vựng", "Phát âm", "Nghe", "Nói", "Đọc", "Viết"};
        ArrayAdapter<String> categoryAdapter = new ArrayAdapter<>(this, android.R.layout.simple_spinner_item, categories);
        categoryAdapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinnerCategory.setAdapter(categoryAdapter);

        // Setup level spinner
        String[] levels = {"Tất cả", "Beginner", "Intermediate", "Advanced"};
        ArrayAdapter<String> levelAdapter = new ArrayAdapter<>(this, android.R.layout.simple_spinner_item, levels);
        levelAdapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinnerLevel.setAdapter(levelAdapter);

        // Set current selections
        spinnerCategory.setSelection(getCategoryPosition(selectedCategory));
        spinnerLevel.setSelection(getLevelPosition(selectedLevel));

        AlertDialog dialog = builder.setView(dialogView).create();

        btnApply.setOnClickListener(v -> {
            selectedCategory = spinnerCategory.getSelectedItem().toString();
            selectedLevel = spinnerLevel.getSelectedItem().toString();
            filterCourses();
            updateFilterButtonText();
            dialog.dismiss();
            Toast.makeText(this, "Đã áp dụng bộ lọc", Toast.LENGTH_SHORT).show();
        });

        btnReset.setOnClickListener(v -> {
            selectedCategory = "Tất cả";
            selectedLevel = "Tất cả";
            spinnerCategory.setSelection(0);
            spinnerLevel.setSelection(0);
            filterCourses();
            updateFilterButtonText();
            dialog.dismiss();
            Toast.makeText(this, "Đã reset bộ lọc", Toast.LENGTH_SHORT).show();
        });

        dialog.show();
    }

    private int getCategoryPosition(String category) {
        String[] categories = {"Tất cả", "Ngữ pháp", "Từ vựng", "Phát âm", "Nghe", "Nói", "Đọc", "Viết"};
        for (int i = 0; i < categories.length; i++) {
            if (categories[i].equals(category)) {
                return i;
            }
        }
        return 0;
    }

    private int getLevelPosition(String level) {
        String[] levels = {"Tất cả", "Beginner", "Intermediate", "Advanced"};
        for (int i = 0; i < levels.length; i++) {
            if (levels[i].equals(level)) {
                return i;
            }
        }
        return 0;
    }

    private void updateFilterButtonText() {
        boolean hasFilters = !selectedCategory.equals("Tất cả") || !selectedLevel.equals("Tất cả");
        if (hasFilters) {
            btnFilter.setText(" Lọc (" + getActiveFilterCount() + ")");
        } else {
            btnFilter.setText(" Lọc");
        }
    }

    private int getActiveFilterCount() {
        int count = 0;
        if (!selectedCategory.equals("Tất cả")) count++;
        if (!selectedLevel.equals("Tất cả")) count++;
        return count;
    }

    private void filterCourses() {
        filteredCourseList.clear();

        for (Course course : courseList) {
            boolean matchesSearch = searchQuery.isEmpty() ||
                course.getTitle().toLowerCase().contains(searchQuery.toLowerCase()) ||
                course.getDescription().toLowerCase().contains(searchQuery.toLowerCase());

            boolean matchesCategory = selectedCategory.equals("Tất cả") ||
                course.getCategory().equals(selectedCategory);

            boolean matchesLevel = selectedLevel.equals("Tất cả") ||
                course.getLevel().equals(selectedLevel);

            if (matchesSearch && matchesCategory && matchesLevel) {
                filteredCourseList.add(course);
            }
        }

        // Update adapter with filtered list
        courseAdapter.updateCourseList(filteredCourseList);
        updateUI();
    }

    private void updateUI() {
        if (filteredCourseList.isEmpty()) {
            tvNoCourses.setVisibility(View.VISIBLE);
            rvCourses.setVisibility(View.GONE);
        } else {
            tvNoCourses.setVisibility(View.GONE);
            rvCourses.setVisibility(View.VISIBLE);
        }

        // Update course count
        if (tvCourseCount != null) {
            String countText = filteredCourseList.size() + " khóa học";
            if (!searchQuery.isEmpty() || !selectedCategory.equals("Tất cả") || !selectedLevel.equals("Tất cả")) {
                countText += " (đã lọc)";
            }
            tvCourseCount.setText(countText);
        }
    }

    private void loadStatistics() {
        if (mAuth.getCurrentUser() == null) return;

        String currentUserId = mAuth.getCurrentUser().getUid();

        // Load total students count từ courseRequests với status "approved"
        loadTotalStudentsFromApprovedRequests(currentUserId);

        // Set average rating (placeholder)
        if (tvAvgRating != null) {
            tvAvgRating.setText("4.8");
        }
    }

    private void loadTotalStudentsFromApprovedRequests(String teacherId) {
        Log.d("CourseManagement", "Loading total students from approved requests for teacherId: " + teacherId);
        
        // Lấy tất cả approved requests từ courseRequests
        db.collection("courseRequests")
                .whereEqualTo("status", "approved")
                .get()
                .addOnSuccessListener(approvedRequests -> {
                    Log.d("CourseManagement", "Found " + approvedRequests.size() + " approved requests total");
                    
                    if (approvedRequests.isEmpty()) {
                        Log.d("CourseManagement", "No approved requests found, setting students count to 0");
                        if (tvTotalStudents != null) {
                            tvTotalStudents.setText("0");
                        }
                        return;
                    }
                    
                    // Lấy danh sách courseIds của teacher này
                    db.collection("courses")
                            .whereEqualTo("teacherId", teacherId)
                            .get()
                            .addOnSuccessListener(teacherCourses -> {
                                Log.d("CourseManagement", "Teacher has " + teacherCourses.size() + " courses");
                                
                                // Tạo Set chứa courseIds của teacher
                                java.util.Set<String> teacherCourseIds = new java.util.HashSet<>();
                                for (com.google.firebase.firestore.QueryDocumentSnapshot courseDoc : teacherCourses) {
                                    teacherCourseIds.add(courseDoc.getId());
                                    Log.d("CourseManagement", "Teacher course ID: " + courseDoc.getId());
                                }
                                
                                // Tạo Set để lưu unique studentId của teacher này
                                java.util.Set<String> uniqueStudentIds = new java.util.HashSet<>();
                                
                                // Duyệt qua tất cả approved requests
                                for (com.google.firebase.firestore.QueryDocumentSnapshot requestDoc : approvedRequests) {
                                    String studentName = requestDoc.getString("studentName");
                                    String studentId = requestDoc.getString("studentId");
                                    String courseId = requestDoc.getString("courseId");
                                    String courseName = requestDoc.getString("courseName");
                                    
                                    Log.d("CourseManagement", "Processing approved request - Student: " + studentName + 
                                          ", StudentId: " + studentId + ", CourseId: " + courseId + ", Course: " + courseName);
                                    
                                    // Chỉ đếm nếu courseId thuộc về teacher này
                                    if (teacherCourseIds.contains(courseId)) {
                                        if (studentId != null && !studentId.isEmpty()) {
                                            uniqueStudentIds.add(studentId);
                                            Log.d("CourseManagement", "Added studentId: " + studentId + 
                                                  " for teacher's course: " + courseName + ". Total unique students: " + uniqueStudentIds.size());
                                        }
                                    } else {
                                        Log.d("CourseManagement", "Skipping request for course not belonging to this teacher: " + courseName);
                                    }
                                }
                                
                                // Cập nhật UI
                                int finalCount = uniqueStudentIds.size();
                                Log.d("CourseManagement", "Final unique students count for teacher: " + finalCount);
                                
                                runOnUiThread(() -> {
                                    if (tvTotalStudents != null) {
                                        tvTotalStudents.setText(String.valueOf(finalCount));
                                    }
                                });
                            })
                            .addOnFailureListener(e -> {
                                Log.e("CourseManagement", "Error loading teacher courses", e);
                                if (tvTotalStudents != null) {
                                    tvTotalStudents.setText("0");
                                }
                            });
                })
                .addOnFailureListener(e -> {
                    Log.e("CourseManagement", "Error loading approved requests", e);
                    if (tvTotalStudents != null) {
                        tvTotalStudents.setText("0");
                    }
                });
    }

    private void loadCourses() {
        // Check if user is authenticated
        if (mAuth.getCurrentUser() == null) {
            Toast.makeText(this, "Bạn cần đăng nhập để xem khóa học", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        String currentUserId = mAuth.getCurrentUser().getUid();

        db.collection("courses")
            .whereEqualTo("teacherId", currentUserId)
            .get()
            .addOnCompleteListener(task -> {
                if (task.isSuccessful()) {
                    courseList.clear();

                    for (QueryDocumentSnapshot document : task.getResult()) {
                        Course course = document.toObject(Course.class);
                        course.setId(document.getId());
                        courseList.add(course);
                    }

                    // Update total courses count
                    if (tvTotalCourses != null) {
                        tvTotalCourses.setText(String.valueOf(courseList.size()));
                    }

                    // Apply current filters
                    filterCourses();

                } else {
                    android.util.Log.e("CourseManagement", "Error loading courses", task.getException());
                    Toast.makeText(this, "Lỗi khi tải khóa học: " + task.getException().getMessage(), Toast.LENGTH_SHORT).show();
                }
            });
    }

    private void setupClickListeners() {
        fabAddCourse.setOnClickListener(v -> {
            Intent intent = new Intent(this, CreateCourseActivity.class);
            startActivity(intent);
        });

        // Add click listener for create first course button in empty state
        MaterialButton btnCreateFirstCourse = findViewById(R.id.btn_create_first_course);
        if (btnCreateFirstCourse != null) {
            btnCreateFirstCourse.setOnClickListener(v -> {
                Intent intent = new Intent(this, CreateCourseActivity.class);
                startActivity(intent);
            });
        }
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.course_management_menu, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        int itemId = item.getItemId();

        if (itemId == android.R.id.home) {
            finish();
            return true;
        } else if (itemId == R.id.action_refresh) {
            loadCourses();
            return true;
        } else if (itemId == R.id.action_search) {
            // Focus on search input
            if (etSearch != null) {
                etSearch.requestFocus();
            }
            return true;
        }

        return super.onOptionsItemSelected(item);
    }

    @Override
    protected void onResume() {
        super.onResume();
        loadCourses(); // Refresh data when returning to this activity
    }
}

package com.vantinh.tienganh;

import android.os.Bundle;
import android.util.Log;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.DocumentSnapshot;
import com.vantinh.tienganh.utils.RealtimeManager;
import java.util.ArrayList;
import java.util.Date;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class CourseRequestManagementActivity extends AppCompatActivity implements CourseRequestAdapter.OnRequestActionListener {

    private RecyclerView recyclerView;
    private CourseRequestAdapter adapter;
    private List<CourseRequest> requestList;
    private FirebaseFirestore db;
    private FirebaseAuth auth;
    private RealtimeManager realtimeManager;
    private String currentTeacherId; // Đổi từ currentTeacherName sang currentTeacherId

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_course_request_management);

        initViews();
        setupToolbar();
        initFirebase();
        getCurrentTeacherInfo();
    }

    private void initViews() {
        recyclerView = findViewById(R.id.recycler_view_requests);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

        requestList = new ArrayList<>();
        adapter = new CourseRequestAdapter(requestList, this);
        recyclerView.setAdapter(adapter);

        // Bỏ các debug buttons - không cần thiết nữa
    }

    // Debug method được cải thiện để kiểm tra tất cả dữ liệu
    private void checkAllDataInFirebase() {
        Log.d("CourseRequestManagement", "=== DEBUGGING: Current teacherId: " + currentTeacherId + " ===");

        // Bước 1: Kiểm tra tất cả courseRequests trước
        db.collection("courseRequests")
                .get()
                .addOnSuccessListener(allRequestDocs -> {
                    Log.d("CourseRequestManagement", "TOTAL courseRequests in Firebase: " + allRequestDocs.size());

                    // Hiển thị tất cả requests để debug
                    StringBuilder allRequestsInfo = new StringBuilder();
                    int matchingCount = 0;

                    for (DocumentSnapshot doc : allRequestDocs.getDocuments()) {
                        String docTeacherId = doc.getString("teacherId");
                        String studentName = doc.getString("studentName");
                        String courseName = doc.getString("courseName");
                        String status = doc.getString("status");

                        Log.d("CourseRequestManagement", "Request: " + studentName +
                            " -> " + courseName + " [status: " + status + "] [teacherId: " + docTeacherId + "]");

                        allRequestsInfo.append("• ").append(studentName).append(" - ").append(courseName)
                            .append(" (Teacher: ").append(docTeacherId).append(")\n");

                        // Kiểm tra xem có match với current teacherId không
                        if (currentTeacherId != null && currentTeacherId.equals(docTeacherId)) {
                            matchingCount++;
                        }
                    }

                    // Hiển thị kết quả debug
                    final int finalMatchingCount = matchingCount; // Tạo biến final để dùng trong lambda
                    final String debugMessage = "=== DEBUG INFO ===\n" +
                        "Current Teacher ID: " + currentTeacherId + "\n" +
                        "Total Requests: " + allRequestDocs.size() + "\n" +
                        "Matching Requests: " + finalMatchingCount + "\n\n" +
                        "All Requests:\n" + allRequestsInfo.toString();

                    runOnUiThread(() -> {
                        // Hiển thị trong dialog để dễ đọc
                        new androidx.appcompat.app.AlertDialog.Builder(CourseRequestManagementActivity.this)
                            .setTitle("Firebase Debug Info")
                            .setMessage(debugMessage)
                            .setPositiveButton("OK", null)
                            .show();

                        Toast.makeText(CourseRequestManagementActivity.this,
                            "DEBUG: Found " + allRequestDocs.size() + " total, " + finalMatchingCount + " matching",
                            Toast.LENGTH_LONG).show();
                    });

                    // Bước 2: Kiểm tra courses collection để verify teacherId
                    checkTeacherCourses();
                })
                .addOnFailureListener(e -> {
                    Log.e("CourseRequestManagement", "Error checking all courseRequests", e);
                    runOnUiThread(() -> {
                        Toast.makeText(this, "DEBUG ERROR: " + e.getMessage(), Toast.LENGTH_LONG).show();
                    });
                });
    }

    // Method mới để kiểm tra courses của teacher
    private void checkTeacherCourses() {
        Log.d("CourseRequestManagement", "=== DEBUGGING: Checking teacher's courses ===");

        db.collection("courses")
                .whereEqualTo("teacherId", currentTeacherId)
                .get()
                .addOnSuccessListener(courseDocs -> {
                    Log.d("CourseRequestManagement", "Found " + courseDocs.size() + " courses for teacherId: " + currentTeacherId);

                    StringBuilder coursesInfo = new StringBuilder();
                    for (DocumentSnapshot doc : courseDocs.getDocuments()) {
                        String title = doc.getString("title");
                        String courseId = doc.getId();
                        coursesInfo.append("• ").append(title).append(" (ID: ").append(courseId).append(")\n");

                        Log.d("CourseRequestManagement", "Teacher's course: " + title + " [ID: " + courseId + "]");
                    }

                    runOnUiThread(() -> {
                        if (courseDocs.isEmpty()) {
                            Toast.makeText(this, "DEBUG: Teacher has NO courses!", Toast.LENGTH_LONG).show();
                        } else {
                            Toast.makeText(this, "DEBUG: Teacher has " + courseDocs.size() + " courses", Toast.LENGTH_LONG).show();
                        }
                    });
                })
                .addOnFailureListener(e -> {
                    Log.e("CourseRequestManagement", "Error checking teacher courses", e);
                });
    }

    private void setupToolbar() {
        Toolbar toolbar = findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setDisplayHomeAsUpEnabled(true);
            getSupportActionBar().setTitle("Yêu cầu tham gia khóa học");
        }
        toolbar.setNavigationOnClickListener(v -> finish());
    }

    private void initFirebase() {
        db = FirebaseFirestore.getInstance();
        auth = FirebaseAuth.getInstance();
        realtimeManager = RealtimeManager.getInstance();
    }

    private void getCurrentTeacherInfo() {
        String currentUserId = auth.getCurrentUser().getUid();
        currentTeacherId = currentUserId; // Sử dụng Firebase UID làm teacherId

        Log.d("CourseRequestManagement", "Current teacherId: " + currentTeacherId);
        setupRealtimeRequests(); // Setup ngay lập tức với teacherId
    }

    // Sử dụng RealtimeManager với teacherId
    private void setupRealtimeRequests() {
        if (currentTeacherId == null) {
            Log.e("CourseRequestManagement", "TeacherId is null - cannot setup listeners");
            Toast.makeText(this, "Không thể tải thông tin giáo viên", Toast.LENGTH_SHORT).show();
            return;
        }

        Log.d("CourseRequestManagement", "Setting up real-time listener for teacherId: '" + currentTeacherId + "'");

        // Load trực tiếp với teacherId
        loadRequestsDirectly();

        realtimeManager.listenToCourseRequests(currentTeacherId,
            new RealtimeManager.OnDataChangeListener<CourseRequest>() {
                @Override
                public void onDataChanged(List<CourseRequest> data) {
                    Log.d("CourseRequestManagement", "Real-time update received: " + data.size() + " requests");

                    requestList.clear();
                    requestList.addAll(data);

                    // Update UI với animation
                    runOnUiThread(() -> {
                        adapter.updateList(requestList);

                        Log.d("CourseRequestManagement", "Updated RecyclerView with " + requestList.size() + " items");

                        // Fade in animation cho RecyclerView
                        recyclerView.setAlpha(0f);
                        recyclerView.animate()
                                .alpha(1f)
                                .setDuration(300)
                                .start();
                    });

                    // Show notification cho yêu cầu
                    if (!data.isEmpty()) {
                        showNewRequestNotification(data.size());
                    } else {
                        Log.d("CourseRequestManagement", "No requests found for teacherId: " + currentTeacherId);
                        runOnUiThread(() -> {
                            Toast.makeText(CourseRequestManagementActivity.this, "Chưa có yêu cầu nào", Toast.LENGTH_SHORT).show();
                        });
                    }
                }

                @Override
                public void onError(Exception e) {
                    Log.e("CourseRequestManagement", "Real-time error: " + e.getMessage(), e);
                    runOnUiThread(() -> {
                        Toast.makeText(CourseRequestManagementActivity.this,
                            "Lỗi real-time: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                    });

                    // Fallback to direct loading
                    loadRequestsDirectly();
                }
            });
    }

    // Method fallback với teacherId - chỉ lấy pending requests
    private void loadRequestsDirectly() {
        Log.d("CourseRequestManagement", "Loading PENDING course requests only");

        db.collection("courseRequests")
                .whereEqualTo("status", "pending")  // Chỉ lấy pending requests
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    requestList.clear();
                    int totalDocs = queryDocumentSnapshots.size();

                    Log.d("CourseRequestManagement", "Found " + totalDocs + " pending requests");

                    for (DocumentSnapshot doc : queryDocumentSnapshots.getDocuments()) {
                        CourseRequest request = doc.toObject(CourseRequest.class);
                        if (request != null && "pending".equals(request.getStatus())) {
                            requestList.add(request);
                            Log.d("CourseRequestManagement", "Added pending request from: " + request.getStudentName() +
                                " for course: " + request.getCourseName() + " with status: " + request.getStatus());
                        }
                    }

                    Log.d("CourseRequestManagement", "Loaded " + requestList.size() + " pending requests");

                    runOnUiThread(() -> {
                        adapter.updateList(requestList);
                        if (requestList.isEmpty()) {
                            Toast.makeText(this, "Không có yêu cầu pending nào", Toast.LENGTH_SHORT).show();
                        } else {
                            Toast.makeText(this, "Hiển thị " + requestList.size() + " yêu cầu pending", Toast.LENGTH_SHORT).show();
                        }

                        // Cập nhật title
                        if (getSupportActionBar() != null) {
                            getSupportActionBar().setTitle("Yêu cầu tham gia (" + requestList.size() + ")");
                        }
                    });
                })
                .addOnFailureListener(e -> {
                    Log.e("CourseRequestManagement", "Error loading pending requests", e);
                    runOnUiThread(() -> {
                        Toast.makeText(this, "Lỗi khi tải dữ liệu: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                    });
                });
    }

    private void showNewRequestNotification(int count) {
        // Hiển thị notification subtle về số lượng yêu cầu
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Yêu cầu tham gia (" + count + ")");
        }
    }

    @Override
    public void onApprove(CourseRequest request) {
        updateRequestStatus(request, "approved");
        createEnrollment(request);
    }

    @Override
    public void onReject(CourseRequest request) {
        updateRequestStatus(request, "rejected");
    }

    private void updateRequestStatus(CourseRequest request, String status) {
        // Tìm document trong courseRequests để cập nhật
        db.collection("courseRequests")
                .whereEqualTo("studentId", request.getStudentId())
                .whereEqualTo("courseId", request.getCourseId())
                .whereEqualTo("status", "pending")
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    if (!queryDocumentSnapshots.isEmpty()) {
                        DocumentSnapshot doc = queryDocumentSnapshots.getDocuments().get(0);
                        db.collection("courseRequests").document(doc.getId())
                                .update("status", status)
                                .addOnSuccessListener(aVoid -> {
                                    // Cập nhật Firebase thành công, giờ cập nhật UI ngay lập tức
                                    removeRequestFromList(request);

                                    Toast.makeText(this,
                                        status.equals("approved") ? "Đã phê duyệt yêu cầu" : "Đã từ chối yêu cầu",
                                        Toast.LENGTH_SHORT).show();
                                })
                                .addOnFailureListener(e -> {
                                    Log.e("CourseRequestManagement", "Error updating request status", e);
                                    Toast.makeText(this, "Lỗi khi cập nhật trạng thái", Toast.LENGTH_SHORT).show();
                                });
                    }
                })
                .addOnFailureListener(e -> {
                    Log.e("CourseRequestManagement", "Error finding request", e);
                });
    }

    private void removeRequestFromList(CourseRequest request) {
        // Tìm và loại bỏ request khỏi danh sách
        for (int i = 0; i < requestList.size(); i++) {
            CourseRequest item = requestList.get(i);
            if (item.getStudentId().equals(request.getStudentId()) &&
                item.getCourseId().equals(request.getCourseId())) {

                // Tạo biến final để sử dụng trong lambda
                final int indexToRemove = i;

                // Loại bỏ item khỏi danh sách
                requestList.remove(i);

                // Cập nhật adapter ngay lập tức
                runOnUiThread(() -> {
                    adapter.notifyItemRemoved(indexToRemove);

                    // Cập nhật title với số lượng mới
                    if (getSupportActionBar() != null) {
                        getSupportActionBar().setTitle("Yêu cầu tham gia (" + requestList.size() + ")");
                    }

                    // Hiển thị thông báo nếu không còn yêu cầu nào
                    if (requestList.isEmpty()) {
                        Toast.makeText(CourseRequestManagementActivity.this, "Đã xử lý hết tất cả yêu cầu", Toast.LENGTH_SHORT).show();
                    }
                });

                Log.d("CourseRequestManagement", "Removed request from list - Student: " +
                      request.getStudentName() + ", Course: " + request.getCourseName());
                break;
            }
        }
    }

    private void createEnrollment(CourseRequest request) {
        Map<String, Object> enrollment = new HashMap<>();
        enrollment.put("courseID", request.getCourseId());        // Sửa từ "courseId" thành "courseID"
        enrollment.put("courseName", request.getCourseName());
        enrollment.put("enrollmentDate", new Date());
        enrollment.put("studentEmail", request.getStudentEmail());
        enrollment.put("studentID", request.getStudentId());      // Sửa từ "studentId" thành "studentID"
        enrollment.put("fullName", request.getStudentName());

        db.collection("enrollments")
                .add(enrollment)
                .addOnSuccessListener(documentReference -> {
                    Log.d("CourseRequestManagement", "Enrollment created successfully: " + documentReference.getId());
                    Log.d("CourseRequestManagement", "Created enrollment - CourseID: " + request.getCourseId() + ", StudentID: " + request.getStudentId() + ", Name: " + request.getStudentName());
                })
                .addOnFailureListener(e -> {
                    Log.e("CourseRequestManagement", "Error creating enrollment", e);
                });
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
        // Cleanup listeners
        if (realtimeManager != null) {
            realtimeManager.removeAllListeners();
        }
    }
}

