package com.vantinh.tienganh;

import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.Button;
import android.widget.RadioButton;
import android.widget.RadioGroup;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import com.google.android.material.textfield.TextInputEditText;
import com.google.firebase.firestore.FirebaseFirestore;
import com.vantinh.tienganh.models.SimpleTestQuestion;
import java.util.ArrayList;
import java.util.List;

public class EditSingleTestQuestionActivity extends AppCompatActivity {

    private static final String TAG = "EditSingleTestQuestion";

    private Toolbar toolbar;
    private TextInputEditText etQuestion, etOptionA, etOptionB, etOptionC, etOptionD;
    private RadioGroup rgCorrectAnswer;
    private RadioButton rbOptionA, rbOptionB, rbOptionC, rbOptionD;
    private Button btnSaveQuestion, btnDeleteQuestion;

    private FirebaseFirestore db;
    private String courseId, courseName, questionId;
    private SimpleTestQuestion currentQuestion;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_edit_single_test_question);

        initViews();
        setupToolbar();
        initFirebase();
        getQuestionInfo();
        setupClickListeners();
        loadQuestionData();
    }

    private void initViews() {
        toolbar = findViewById(R.id.toolbar);
        etQuestion = findViewById(R.id.et_question);
        etOptionA = findViewById(R.id.et_option_a);
        etOptionB = findViewById(R.id.et_option_b);
        etOptionC = findViewById(R.id.et_option_c);
        etOptionD = findViewById(R.id.et_option_d);
        rgCorrectAnswer = findViewById(R.id.rg_correct_answer);
        rbOptionA = findViewById(R.id.rb_option_a);
        rbOptionB = findViewById(R.id.rb_option_b);
        rbOptionC = findViewById(R.id.rb_option_c);
        rbOptionD = findViewById(R.id.rb_option_d);
        btnSaveQuestion = findViewById(R.id.btn_save_question);
        btnDeleteQuestion = findViewById(R.id.btn_delete_question);
    }

    private void setupToolbar() {
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Chỉnh sửa câu hỏi");
            getSupportActionBar().setDisplayHomeAsUpEnabled(true);
        }
    }

    private void initFirebase() {
        db = FirebaseFirestore.getInstance();
    }

    private void getQuestionInfo() {
        courseId = getIntent().getStringExtra("courseId");
        courseName = getIntent().getStringExtra("courseName");
        questionId = getIntent().getStringExtra("questionId");

        if (courseName != null) {
            getSupportActionBar().setSubtitle(courseName);
        }
    }

    private void setupClickListeners() {
        btnSaveQuestion.setOnClickListener(v -> saveQuestion());
        btnDeleteQuestion.setOnClickListener(v -> deleteQuestion());
    }

    private void loadQuestionData() {
        if (questionId == null) {
            Toast.makeText(this, "Lỗi: Không tìm thấy ID câu hỏi", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        Log.d(TAG, "Loading question data for ID: " + questionId);

        db.collection("test")
                .document(questionId)
                .get()
                .addOnSuccessListener(documentSnapshot -> {
                    if (documentSnapshot.exists()) {
                        currentQuestion = documentSnapshot.toObject(SimpleTestQuestion.class);
                        if (currentQuestion != null) {
                            currentQuestion.setDocumentId(documentSnapshot.getId());
                            populateFields();
                        }
                    } else {
                        Toast.makeText(this, "Không tìm thấy câu hỏi", Toast.LENGTH_SHORT).show();
                        finish();
                    }
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "Error loading question", e);
                    Toast.makeText(this, "Lỗi khi tải câu hỏi: " + e.getMessage(), 
                                 Toast.LENGTH_SHORT).show();
                    finish();
                });
    }

    private void populateFields() {
        if (currentQuestion == null) return;

        // Điền nội dung câu hỏi
        etQuestion.setText(currentQuestion.getQuestion());

        // Điền các lựa chọn
        List<String> options = currentQuestion.getCorrectAnswer();
        if (options != null && options.size() >= 4) {
            etOptionA.setText(options.get(0));
            etOptionB.setText(options.get(1));
            etOptionC.setText(options.get(2));
            etOptionD.setText(options.get(3));
        }

        // Chọn đáp án đúng
        int correctIndex = currentQuestion.getOptions();
        switch (correctIndex) {
            case 0:
                rbOptionA.setChecked(true);
                break;
            case 1:
                rbOptionB.setChecked(true);
                break;
            case 2:
                rbOptionC.setChecked(true);
                break;
            case 3:
                rbOptionD.setChecked(true);
                break;
        }

        Log.d(TAG, "Populated fields for question: " + currentQuestion.getQuestion());
    }

    private void saveQuestion() {
        if (!validateInput()) {
            return;
        }

        String questionText = etQuestion.getText().toString().trim();
        List<String> options = new ArrayList<>();
        options.add(etOptionA.getText().toString().trim());
        options.add(etOptionB.getText().toString().trim());
        options.add(etOptionC.getText().toString().trim());
        options.add(etOptionD.getText().toString().trim());

        int correctAnswerIndex = getCorrectAnswerIndex();

        // Cập nhật currentQuestion
        currentQuestion.setQuestion(questionText);
        currentQuestion.setCorrectAnswer(options);
        currentQuestion.setOptions(correctAnswerIndex);

        btnSaveQuestion.setEnabled(false);
        btnSaveQuestion.setText("Đang lưu...");

        Log.d(TAG, "Saving question: " + currentQuestion.toString());

        db.collection("test")
                .document(questionId)
                .set(currentQuestion)
                .addOnSuccessListener(aVoid -> {
                    Log.d(TAG, "Question saved successfully");
                    Toast.makeText(this, "Đã lưu câu hỏi", Toast.LENGTH_SHORT).show();
                    finish();
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "Error saving question", e);
                    Toast.makeText(this, "Lỗi khi lưu câu hỏi: " + e.getMessage(), 
                                 Toast.LENGTH_SHORT).show();
                    btnSaveQuestion.setEnabled(true);
                    btnSaveQuestion.setText("Lưu thay đổi");
                });
    }

    private void deleteQuestion() {
        new androidx.appcompat.app.AlertDialog.Builder(this)
                .setTitle("Xác nhận xóa câu hỏi")
                .setMessage("Bạn có chắc chắn muốn xóa câu hỏi này?\n\nHành động này không thể hoàn tác.")
                .setPositiveButton("Xóa", (dialog, which) -> performDeleteQuestion())
                .setNegativeButton("Hủy", null)
                .setIcon(android.R.drawable.ic_dialog_alert)
                .show();
    }

    private void performDeleteQuestion() {
        btnDeleteQuestion.setEnabled(false);
        btnDeleteQuestion.setText("Đang xóa...");

        db.collection("test")
                .document(questionId)
                .delete()
                .addOnSuccessListener(aVoid -> {
                    Log.d(TAG, "Question deleted successfully");
                    Toast.makeText(this, "Đã xóa câu hỏi", Toast.LENGTH_SHORT).show();
                    finish();
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "Error deleting question", e);
                    Toast.makeText(this, "Lỗi khi xóa câu hỏi: " + e.getMessage(), 
                                 Toast.LENGTH_SHORT).show();
                    btnDeleteQuestion.setEnabled(true);
                    btnDeleteQuestion.setText("Xóa câu hỏi");
                });
    }

    private boolean validateInput() {
        String questionText = etQuestion.getText().toString().trim();
        if (questionText.isEmpty()) {
            etQuestion.setError("Vui lòng nhập nội dung câu hỏi");
            etQuestion.requestFocus();
            return false;
        }

        String[] options = {
            etOptionA.getText().toString().trim(),
            etOptionB.getText().toString().trim(),
            etOptionC.getText().toString().trim(),
            etOptionD.getText().toString().trim()
        };

        for (int i = 0; i < options.length; i++) {
            if (options[i].isEmpty()) {
                switch (i) {
                    case 0:
                        etOptionA.setError("Vui lòng nhập lựa chọn A");
                        etOptionA.requestFocus();
                        break;
                    case 1:
                        etOptionB.setError("Vui lòng nhập lựa chọn B");
                        etOptionB.requestFocus();
                        break;
                    case 2:
                        etOptionC.setError("Vui lòng nhập lựa chọn C");
                        etOptionC.requestFocus();
                        break;
                    case 3:
                        etOptionD.setError("Vui lòng nhập lựa chọn D");
                        etOptionD.requestFocus();
                        break;
                }
                return false;
            }
        }

        if (rgCorrectAnswer.getCheckedRadioButtonId() == -1) {
            Toast.makeText(this, "Vui lòng chọn đáp án đúng", Toast.LENGTH_SHORT).show();
            return false;
        }

        return true;
    }

    private int getCorrectAnswerIndex() {
        int checkedId = rgCorrectAnswer.getCheckedRadioButtonId();

        if (checkedId == rbOptionA.getId()) {
            return 0;
        } else if (checkedId == rbOptionB.getId()) {
            return 1;
        } else if (checkedId == rbOptionC.getId()) {
            return 2;
        } else if (checkedId == rbOptionD.getId()) {
            return 3;
        }

        return -1;
    }

    @Override
    public boolean onSupportNavigateUp() {
        onBackPressed();
        return true;
    }
}

package com.vantinh.tienganh;

import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.ProgressBar;
import android.widget.TextView;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.QueryDocumentSnapshot;
import com.vantinh.tienganh.adapters.EditTestQuestionAdapter;
import com.vantinh.tienganh.models.SimpleTestQuestion;
import java.util.ArrayList;
import java.util.List;

public class EditTestQuestionsActivity extends AppCompatActivity {

    private static final String TAG = "EditTestQuestions";

    private Toolbar toolbar;
    private RecyclerView recyclerView;
    private ProgressBar progressBar;
    private TextView tvNoQuestions;
    private EditTestQuestionAdapter adapter;

    private FirebaseFirestore db;
    private String courseId;
    private String courseName;
    private List<SimpleTestQuestion> testQuestions;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_edit_test_questions);

        initViews();
        setupToolbar();
        initFirebase();
        getCourseInfo();
        setupRecyclerView();
        loadTestQuestions();
    }

    private void initViews() {
        toolbar = findViewById(R.id.toolbar);
        recyclerView = findViewById(R.id.rv_test_questions);
        progressBar = findViewById(R.id.progress_bar);
        tvNoQuestions = findViewById(R.id.tv_no_questions);
    }

    private void setupToolbar() {
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Chỉnh sửa bài kiểm tra");
            getSupportActionBar().setDisplayHomeAsUpEnabled(true);
        }
    }

    private void initFirebase() {
        db = FirebaseFirestore.getInstance();
    }

    private void getCourseInfo() {
        courseId = getIntent().getStringExtra("courseId");
        courseName = getIntent().getStringExtra("courseName");

        if (courseName != null) {
            getSupportActionBar().setSubtitle(courseName);
        }
    }

    private void setupRecyclerView() {
        testQuestions = new ArrayList<>();
        adapter = new EditTestQuestionAdapter(testQuestions, this::editQuestion, this::deleteQuestion);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
        recyclerView.setAdapter(adapter);
    }

    private void loadTestQuestions() {
        progressBar.setVisibility(View.VISIBLE);
        recyclerView.setVisibility(View.GONE);
        tvNoQuestions.setVisibility(View.GONE);

        Log.d(TAG, "Loading test questions for courseId: " + courseId);

        db.collection("test")
                .whereEqualTo("courseId", courseId)
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    testQuestions.clear();

                    for (QueryDocumentSnapshot document : queryDocumentSnapshots) {
                        SimpleTestQuestion question = document.toObject(SimpleTestQuestion.class);
                        question.setDocumentId(document.getId()); // Lưu document ID để có thể edit/delete
                        testQuestions.add(question);

                        Log.d(TAG, "Loaded question: " + question.getQuestion());
                    }

                    progressBar.setVisibility(View.GONE);

                    if (testQuestions.isEmpty()) {
                        tvNoQuestions.setVisibility(View.VISIBLE);
                        recyclerView.setVisibility(View.GONE);
                    } else {
                        tvNoQuestions.setVisibility(View.GONE);
                        recyclerView.setVisibility(View.VISIBLE);
                        adapter.notifyDataSetChanged();
                    }

                    Log.d(TAG, "Total questions loaded: " + testQuestions.size());
                })
                .addOnFailureListener(e -> {
                    Log.e(TAG, "Error loading test questions", e);
                    progressBar.setVisibility(View.GONE);
                    tvNoQuestions.setVisibility(View.VISIBLE);
                    tvNoQuestions.setText("Lỗi khi tải danh sách câu hỏi");
                    Toast.makeText(this, "Lỗi khi tải danh sách câu hỏi: " + e.getMessage(), 
                                 Toast.LENGTH_SHORT).show();
                });
    }

    private void editQuestion(SimpleTestQuestion question) {
        // Chuyển đến activity chỉnh sửa câu hỏi
        Intent intent = new Intent(this, EditSingleTestQuestionActivity.class);
        intent.putExtra("courseId", courseId);
        intent.putExtra("courseName", courseName);
        intent.putExtra("questionId", question.getDocumentId());
        intent.putExtra("question", question.getQuestion());
        // Truyền thêm dữ liệu khác nếu cần
        startActivity(intent);
    }

    private void deleteQuestion(SimpleTestQuestion question) {
        // Xóa câu hỏi
        if (question.getDocumentId() != null) {
            db.collection("test")
                    .document(question.getDocumentId())
                    .delete()
                    .addOnSuccessListener(aVoid -> {
                        Log.d(TAG, "Question deleted successfully");
                        Toast.makeText(this, "Đã xóa câu hỏi", Toast.LENGTH_SHORT).show();
                        loadTestQuestions(); // Reload danh sách
                    })
                    .addOnFailureListener(e -> {
                        Log.e(TAG, "Error deleting question", e);
                        Toast.makeText(this, "Lỗi khi xóa câu hỏi: " + e.getMessage(), 
                                     Toast.LENGTH_SHORT).show();
                    });
        }
    }

    @Override
    public boolean onSupportNavigateUp() {
        onBackPressed();
        return true;
    }

    @Override
    protected void onResume() {
        super.onResume();
        // Reload danh sách khi quay lại từ activity chỉnh sửa
        loadTestQuestions();
    }
}

package com.vantinh.tienganh;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import java.util.List;

public class LessonProgressAdapter extends RecyclerView.Adapter<LessonProgressAdapter.LessonProgressViewHolder> {

    private List<StudentProgressDetailActivity.LessonProgressItem> lessonProgressList;

    public LessonProgressAdapter(List<StudentProgressDetailActivity.LessonProgressItem> lessonProgressList) {
        this.lessonProgressList = lessonProgressList;
    }

    @NonNull
    @Override
    public LessonProgressViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_lesson_progress, parent, false);
        return new LessonProgressViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull LessonProgressViewHolder holder, int position) {
        StudentProgressDetailActivity.LessonProgressItem item = lessonProgressList.get(position);

        holder.tvLessonTitle.setText(item.getLessonTitle());
        holder.tvLessonType.setText(item.getLessonType());

        // Set completion status
        if (item.isCompleted()) {
            holder.ivCompletionStatus.setImageResource(R.drawable.ic_check_circle);
            holder.ivCompletionStatus.setColorFilter(holder.itemView.getContext().getColor(android.R.color.holo_green_dark));
            holder.tvCompletionStatus.setText("Đã hoàn thành");
            holder.tvCompletionStatus.setTextColor(holder.itemView.getContext().getColor(android.R.color.holo_green_dark));
        } else {
            holder.ivCompletionStatus.setImageResource(R.drawable.ic_circle_outline);
            holder.ivCompletionStatus.setColorFilter(holder.itemView.getContext().getColor(android.R.color.darker_gray));
            holder.tvCompletionStatus.setText("Chưa hoàn thành");
            holder.tvCompletionStatus.setTextColor(holder.itemView.getContext().getColor(android.R.color.darker_gray));
        }
    }

    @Override
    public int getItemCount() {
        return lessonProgressList != null ? lessonProgressList.size() : 0;
    }

    static class LessonProgressViewHolder extends RecyclerView.ViewHolder {
        TextView tvLessonTitle, tvLessonType, tvCompletionStatus;
        ImageView ivCompletionStatus;

        public LessonProgressViewHolder(@NonNull View itemView) {
            super(itemView);
            tvLessonTitle = itemView.findViewById(R.id.tv_lesson_title);
            tvLessonType = itemView.findViewById(R.id.tv_lesson_type);
            tvCompletionStatus = itemView.findViewById(R.id.tv_completion_status);
            ivCompletionStatus = itemView.findViewById(R.id.iv_completion_status);
        }
    }
}

package com.vantinh.tienganh;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import java.util.List;

public class LessonProgressAdapter extends RecyclerView.Adapter<LessonProgressAdapter.LessonProgressViewHolder> {

    private List<StudentProgressDetailActivity.LessonProgressItem> lessonProgressList;

    public LessonProgressAdapter(List<StudentProgressDetailActivity.LessonProgressItem> lessonProgressList) {
        this.lessonProgressList = lessonProgressList;
    }

    @NonNull
    @Override
    public LessonProgressViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        // Use existing Android layout
        View view = LayoutInflater.from(parent.getContext())
                .inflate(android.R.layout.simple_list_item_2, parent, false);
        return new LessonProgressViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull LessonProgressViewHolder holder, int position) {
        StudentProgressDetailActivity.LessonProgressItem item = lessonProgressList.get(position);

        // Set completion status with emoji icons
        if (item.isCompleted()) {
            holder.tvLessonTitle.setText("✅ " + item.getLessonTitle());
            holder.tvLessonType.setText(item.getLessonType() + " - Đã hoàn thành");
            holder.tvLessonType.setTextColor(holder.itemView.getContext().getColor(android.R.color.holo_green_dark));
        } else {
            holder.tvLessonTitle.setText("⭕ " + item.getLessonTitle());
            holder.tvLessonType.setText(item.getLessonType() + " - Chưa hoàn thành");
            holder.tvLessonType.setTextColor(holder.itemView.getContext().getColor(android.R.color.darker_gray));
        }
    }

    @Override
    public int getItemCount() {
        return lessonProgressList != null ? lessonProgressList.size() : 0;
    }

    static class LessonProgressViewHolder extends RecyclerView.ViewHolder {
        TextView tvLessonTitle, tvLessonType;

        public LessonProgressViewHolder(@NonNull View itemView) {
            super(itemView);
            // Use standard Android layout IDs
            tvLessonTitle = itemView.findViewById(android.R.id.text1);
            tvLessonType = itemView.findViewById(android.R.id.text2);
        }
    }
}

package com.vantinh.tienganh;

import android.content.Intent;
import android.os.Bundle;
import android.text.TextUtils;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.app.AlertDialog;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseAuthInvalidCredentialsException;
import com.google.firebase.auth.FirebaseAuthInvalidUserException;
import com.google.firebase.firestore.FirebaseFirestore;

import java.util.HashMap;
import java.util.Map;

public class LoginActivity extends AppCompatActivity {

    private EditText etEmail, etPassword;
    private Button btnLogin;
    private TextView tvRegister;
    private FirebaseAuth mAuth;
    private FirebaseFirestore db;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_login);

        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();

        initViews();
        setupClickListeners();

        // Pre-fill test account for easier testing
        etEmail.setText("quocthao@gmail.com");
        etPassword.setText("123456");
    }

    private void initViews() {
        etEmail = findViewById(R.id.et_email);
        etPassword = findViewById(R.id.et_password);
        btnLogin = findViewById(R.id.btn_login);
        tvRegister = findViewById(R.id.tv_register);
    }

    private void setupClickListeners() {
        btnLogin.setOnClickListener(v -> handleLogin());

        tvRegister.setOnClickListener(v ->
            startActivity(new Intent(LoginActivity.this, RegisterActivity.class)));

        // Long click to create test account
        btnLogin.setOnLongClickListener(v -> {
            showCreateTestAccountDialog();
            return true;
        });
    }

    private void handleLogin() {
        String email = etEmail.getText() != null ? etEmail.getText().toString().trim() : "";
        String password = etPassword.getText() != null ? etPassword.getText().toString().trim() : "";

        if (validateInput(email, password)) {
            btnLogin.setEnabled(false);
            btnLogin.setText("Đang đăng nhập...");

            android.util.Log.d("LoginActivity", "Attempting to login with email: " + email);

            mAuth.signInWithEmailAndPassword(email, password)
                .addOnCompleteListener(this, task -> {
                    android.util.Log.d("LoginActivity", "Login task completed. Success: " + task.isSuccessful());

                    if (task.isSuccessful() && mAuth.getCurrentUser() != null) {
                        String userId = mAuth.getCurrentUser().getUid();
                        android.util.Log.d("LoginActivity", "Login successful for user: " + userId);
                        getUserRoleAndRedirect(userId);
                    } else {
                        handleLoginError(task.getException());
                    }
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("LoginActivity", "Login failure", e);
                    handleLoginError(e);
                });
        }
    }

    private void handleLoginError(Exception exception) {
        btnLogin.setEnabled(true);
        btnLogin.setText("Đăng nhập");

        String errorMessage = "Đăng nhập thất bại";

        if (exception instanceof FirebaseAuthInvalidUserException) {
            errorMessage = "Tài khoản không tồn tại. Bạn có muốn tạo tài khoản này không?";
            showCreateAccountDialog();
            return;
        } else if (exception instanceof FirebaseAuthInvalidCredentialsException) {
            errorMessage = "Email hoặc mật khẩu không đúng";
        } else if (exception != null) {
            errorMessage = "Lỗi đăng nhập: " + exception.getMessage();
        }

        Toast.makeText(this, errorMessage, Toast.LENGTH_LONG).show();
    }

    private void showCreateAccountDialog() {
        String email = etEmail.getText().toString().trim();
        String password = etPassword.getText().toString().trim();

        new AlertDialog.Builder(this)
                .setTitle("Tạo tài khoản")
                .setMessage("Tài khoản " + email + " chưa tồn tại. Bạn có muốn tạo tài khoản này không?")
                .setPositiveButton("Tạo tài khoản", (dialog, which) -> {
                    createAccount(email, password, "student"); // Default to student
                })
                .setNegativeButton("Hủy", null)
                .show();
    }

    private void showCreateTestAccountDialog() {
        new AlertDialog.Builder(this)
                .setTitle("Tạo tài khoản test")
                .setMessage("Chọn loại tài khoản test muốn tạo:")
                .setPositiveButton("Học viên", (dialog, which) -> {
                    etEmail.setText("student@test.com");
                    etPassword.setText("123456");
                    createAccount("student@test.com", "123456", "student");
                })
                .setNeutralButton("Giáo viên", (dialog, which) -> {
                    etEmail.setText("teacher@test.com");
                    etPassword.setText("123456");
                    createAccount("teacher@test.com", "123456", "teacher");
                })
                .setNegativeButton("Admin", (dialog, which) -> {
                    etEmail.setText("admin@test.com");
                    etPassword.setText("123456");
                    createAccount("admin@test.com", "123456", "admin");
                })
                .show();
    }

    private void createAccount(String email, String password, String role) {
        btnLogin.setEnabled(false);
        btnLogin.setText("Đang tạo tài khoản...");

        mAuth.createUserWithEmailAndPassword(email, password)
                .addOnCompleteListener(task -> {
                    if (task.isSuccessful() && mAuth.getCurrentUser() != null) {
                        String userId = mAuth.getCurrentUser().getUid();
                        saveUserToFirestore(userId, email, role);
                    } else {
                        btnLogin.setEnabled(true);
                        btnLogin.setText("Đăng nhập");
                        String error = task.getException() != null ? task.getException().getMessage() : "Lỗi tạo tài khoản";
                        Toast.makeText(this, "Tạo tài khoản thất bại: " + error, Toast.LENGTH_LONG).show();
                    }
                });
    }

    private void saveUserToFirestore(String userId, String email, String role) {
        // Tạo đối tượng User với chỉ 5 trường dữ liệu theo yêu cầu: ID, address, email, fullName, role
        Map<String, Object> userData = new HashMap<>();
        userData.put("id", userId);
        userData.put("email", email);
        userData.put("fullName", getFullNameFromRole(role)); // Sử dụng fullName thay vì name
        userData.put("address", ""); // Địa chỉ mặc định trống
        userData.put("role", role);

        // Không lưu createdAt, updatedAt để chỉ có đúng 5 trường theo yêu cầu

        db.collection("users").document(userId)
                .set(userData)
                .addOnSuccessListener(aVoid -> {
                    btnLogin.setEnabled(true);
                    btnLogin.setText("Đăng nhập");
                    Toast.makeText(this, "Tạo tài khoản thành công! Đang đăng nhập...", Toast.LENGTH_SHORT).show();
                    android.util.Log.d("LoginActivity", "User saved with 5 fields: " + userData.toString());
                    redirectToRoleActivity(role);
                })
                .addOnFailureListener(e -> {
                    btnLogin.setEnabled(true);
                    btnLogin.setText("Đăng nhập");
                    android.util.Log.e("LoginActivity", "Error saving user to Firestore", e);
                    Toast.makeText(this, "Lỗi lưu thông tin: " + e.getMessage(), Toast.LENGTH_LONG).show();
                });
    }

    private String getFullNameFromRole(String role) {
        switch (role.toLowerCase()) {
            case "admin": return "Quản trị viên";
            case "teacher": return "Giáo viên";
            case "student": return "Học viên";
            default: return "Người dùng";
        }
    }

    private void getUserRoleAndRedirect(String userId) {
        android.util.Log.d("LoginActivity", "Getting user role for: " + userId);

        db.collection("users").document(userId)
                .get()
                .addOnSuccessListener(documentSnapshot -> {
                    btnLogin.setEnabled(true);
                    btnLogin.setText("Đăng nhập");

                    if (documentSnapshot.exists()) {
                        String role = documentSnapshot.getString("role");
                        android.util.Log.d("LoginActivity", "User role found: " + role);

                        if (role != null) {
                            redirectToRoleActivity(role);
                        } else {
                            android.util.Log.w("LoginActivity", "User role is null, defaulting to student");
                            redirectToRoleActivity("student");
                        }
                    } else {
                        android.util.Log.w("LoginActivity", "User document does not exist, creating default student profile");
                        // Create user document if it doesn't exist
                        saveUserToFirestore(userId, mAuth.getCurrentUser().getEmail(), "student");
                    }
                })
                .addOnFailureListener(e -> {
                    btnLogin.setEnabled(true);
                    btnLogin.setText("Đăng nhập");
                    android.util.Log.e("LoginActivity", "Error getting user role", e);
                    Toast.makeText(this, "Lỗi lấy thông tin người dùng: " + e.getMessage(), Toast.LENGTH_LONG).show();
                });
    }

    private void redirectToRoleActivity(String role) {
        android.util.Log.d("LoginActivity", "Redirecting to activity for role: " + role);

        Intent intent;
        switch (role.toLowerCase()) {
            case "admin":
                intent = new Intent(this, AdminDashboardActivity.class);
                android.util.Log.d("LoginActivity", "Redirecting to AdminDashboardActivity");
                break;
            case "teacher":
                intent = new Intent(this, TeacherDashboardActivity.class);
                android.util.Log.d("LoginActivity", "Redirecting to TeacherDashboardActivity");
                break;
            case "student":
            default:
                intent = new Intent(this, StudentDashboardActivity.class);
                android.util.Log.d("LoginActivity", "Redirecting to StudentDashboardActivity");
                break;
        }

        // Clear the activity stack so user can't go back to login
        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
        startActivity(intent);
        finish();
    }

    private boolean validateInput(String email, String password) {
        if (TextUtils.isEmpty(email)) {
            etEmail.setError("Vui lòng nhập email");
            etEmail.requestFocus();
            return false;
        }

        if (!android.util.Patterns.EMAIL_ADDRESS.matcher(email).matches()) {
            etEmail.setError("Email không hợp lệ");
            etEmail.requestFocus();
            return false;
        }

        if (TextUtils.isEmpty(password)) {
            etPassword.setError("Vui lòng nhập mật khẩu");
            etPassword.requestFocus();
            return false;
        }

        if (password.length() < 6) {
            etPassword.setError("Mật khẩu phải có ít nhất 6 ký tự");
            etPassword.requestFocus();
            return false;
        }

        return true;
    }
}

package com.vantinh.tienganh;

import android.content.Intent;
import android.os.Bundle;
import android.text.TextUtils;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.app.AlertDialog;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseAuthInvalidCredentialsException;
import com.google.firebase.auth.FirebaseAuthInvalidUserException;
import com.google.firebase.firestore.FirebaseFirestore;

import java.util.HashMap;
import java.util.Map;

public class LoginActivity extends AppCompatActivity {

    private EditText etEmail, etPassword;
    private Button btnLogin;
    private TextView tvRegister;
    private FirebaseAuth mAuth;
    private FirebaseFirestore db;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_login);

        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();

        initViews();
        setupClickListeners();

        // Removed pre-fill test account for production use
    }

    private void initViews() {
        etEmail = findViewById(R.id.et_email);
        etPassword = findViewById(R.id.et_password);
        btnLogin = findViewById(R.id.btn_login);
        tvRegister = findViewById(R.id.tv_register);
    }

    private void setupClickListeners() {
        btnLogin.setOnClickListener(v -> handleLogin());

        tvRegister.setOnClickListener(v ->
            startActivity(new Intent(LoginActivity.this, RegisterActivity.class)));

        // Long click to create test account
        btnLogin.setOnLongClickListener(v -> {
            showCreateTestAccountDialog();
            return true;
        });
    }

    private void handleLogin() {
        String email = etEmail.getText() != null ? etEmail.getText().toString().trim() : "";
        String password = etPassword.getText() != null ? etPassword.getText().toString().trim() : "";

        if (validateInput(email, password)) {
            btnLogin.setEnabled(false);
            btnLogin.setText("Đang đăng nhập...");

            android.util.Log.d("LoginActivity", "Attempting to login with email: " + email);

            mAuth.signInWithEmailAndPassword(email, password)
                .addOnCompleteListener(this, task -> {
                    android.util.Log.d("LoginActivity", "Login task completed. Success: " + task.isSuccessful());

                    if (task.isSuccessful() && mAuth.getCurrentUser() != null) {
                        String userId = mAuth.getCurrentUser().getUid();
                        android.util.Log.d("LoginActivity", "Login successful for user: " + userId);
                        getUserRoleAndRedirect(userId);
                    } else {
                        handleLoginError(task.getException());
                    }
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("LoginActivity", "Login failure", e);
                    handleLoginError(e);
                });
        }
    }

    private void handleLoginError(Exception exception) {
        btnLogin.setEnabled(true);
        btnLogin.setText("Đăng nhập");

        String errorMessage = "Đăng nhập thất bại";

        if (exception instanceof FirebaseAuthInvalidUserException) {
            errorMessage = "Tài khoản không tồn tại. Bạn có muốn tạo tài khoản này không?";
            showCreateAccountDialog();
            return;
        } else if (exception instanceof FirebaseAuthInvalidCredentialsException) {
            errorMessage = "Email hoặc mật khẩu không đúng";
        } else if (exception != null) {
            errorMessage = "Lỗi đăng nhập: " + exception.getMessage();
        }

        Toast.makeText(this, errorMessage, Toast.LENGTH_LONG).show();
    }

    private void showCreateAccountDialog() {
        String email = etEmail.getText().toString().trim();
        String password = etPassword.getText().toString().trim();

        new AlertDialog.Builder(this)
                .setTitle("Tạo tài khoản")
                .setMessage("Tài khoản " + email + " chưa tồn tại. Bạn có muốn tạo tài khoản này không?")
                .setPositiveButton("Tạo tài khoản", (dialog, which) -> {
                    createAccount(email, password, "student"); // Default to student
                })
                .setNegativeButton("Hủy", null)
                .show();
    }

    private void showCreateTestAccountDialog() {
        new AlertDialog.Builder(this)
                .setTitle("Tạo tài khoản test")
                .setMessage("Chọn loại tài khoản test muốn tạo:")
                .setPositiveButton("Học viên", (dialog, which) -> {
                    etEmail.setText("student@test.com");
                    etPassword.setText("123456");
                    createAccount("student@test.com", "123456", "student");
                })
                .setNeutralButton("Giáo viên", (dialog, which) -> {
                    etEmail.setText("teacher@test.com");
                    etPassword.setText("123456");
                    createAccount("teacher@test.com", "123456", "teacher");
                })
                .setNegativeButton("Admin", (dialog, which) -> {
                    etEmail.setText("admin@test.com");
                    etPassword.setText("123456");
                    createAccount("admin@test.com", "123456", "admin");
                })
                .show();
    }

    private void createAccount(String email, String password, String role) {
        btnLogin.setEnabled(false);
        btnLogin.setText("Đang tạo tài khoản...");

        mAuth.createUserWithEmailAndPassword(email, password)
                .addOnCompleteListener(task -> {
                    if (task.isSuccessful() && mAuth.getCurrentUser() != null) {
                        String userId = mAuth.getCurrentUser().getUid();
                        saveUserToFirestore(userId, email, role);
                    } else {
                        btnLogin.setEnabled(true);
                        btnLogin.setText("Đăng nhập");
                        String error = task.getException() != null ? task.getException().getMessage() : "Lỗi tạo tài khoản";
                        Toast.makeText(this, "Tạo tài khoản thất bại: " + error, Toast.LENGTH_LONG).show();
                    }
                });
    }

    private void saveUserToFirestore(String userId, String email, String role) {
        // Tạo đối tượng User với chỉ 5 trường dữ liệu theo yêu cầu: ID, address, email, fullName, role
        Map<String, Object> userData = new HashMap<>();
        userData.put("id", userId);
        userData.put("email", email);
        userData.put("fullName", getFullNameFromRole(role)); // Sử dụng fullName thay vì name
        userData.put("address", ""); // Địa chỉ mặc định trống
        userData.put("role", role);

        // Không lưu createdAt, updatedAt để chỉ có đúng 5 trường theo yêu cầu

        db.collection("users").document(userId)
                .set(userData)
                .addOnSuccessListener(aVoid -> {
                    btnLogin.setEnabled(true);
                    btnLogin.setText("Đăng nhập");
                    Toast.makeText(this, "Tạo tài khoản thành công! Đang đăng nhập...", Toast.LENGTH_SHORT).show();
                    android.util.Log.d("LoginActivity", "User saved with 5 fields: " + userData.toString());
                    redirectToRoleActivity(role);
                })
                .addOnFailureListener(e -> {
                    btnLogin.setEnabled(true);
                    btnLogin.setText("Đăng nhập");
                    android.util.Log.e("LoginActivity", "Error saving user to Firestore", e);
                    Toast.makeText(this, "Lỗi lưu thông tin: " + e.getMessage(), Toast.LENGTH_LONG).show();
                });
    }

    private String getFullNameFromRole(String role) {
        switch (role.toLowerCase()) {
            case "admin": return "Quản trị viên";
            case "teacher": return "Giáo viên";
            case "student": return "Học viên";
            default: return "Người dùng";
        }
    }

    private void getUserRoleAndRedirect(String userId) {
        android.util.Log.d("LoginActivity", "Getting user role for: " + userId);

        db.collection("users").document(userId)
                .get()
                .addOnSuccessListener(documentSnapshot -> {
                    btnLogin.setEnabled(true);
                    btnLogin.setText("Đăng nhập");

                    if (documentSnapshot.exists()) {
                        String role = documentSnapshot.getString("role");
                        android.util.Log.d("LoginActivity", "User role found: " + role);

                        if (role != null) {
                            redirectToRoleActivity(role);
                        } else {
                            android.util.Log.w("LoginActivity", "User role is null, defaulting to student");
                            redirectToRoleActivity("student");
                        }
                    } else {
                        android.util.Log.w("LoginActivity", "User document does not exist, creating default student profile");
                        // Create user document if it doesn't exist
                        saveUserToFirestore(userId, mAuth.getCurrentUser().getEmail(), "student");
                    }
                })
                .addOnFailureListener(e -> {
                    btnLogin.setEnabled(true);
                    btnLogin.setText("Đăng nhập");
                    android.util.Log.e("LoginActivity", "Error getting user role", e);
                    Toast.makeText(this, "Lỗi lấy thông tin người dùng: " + e.getMessage(), Toast.LENGTH_LONG).show();
                });
    }

    private void redirectToRoleActivity(String role) {
        android.util.Log.d("LoginActivity", "Redirecting to activity for role: " + role);

        Intent intent;
        switch (role.toLowerCase()) {
            case "admin":
                intent = new Intent(this, AdminDashboardActivity.class);
                android.util.Log.d("LoginActivity", "Redirecting to AdminDashboardActivity");
                break;
            case "teacher":
                intent = new Intent(this, TeacherDashboardActivity.class);
                android.util.Log.d("LoginActivity", "Redirecting to TeacherDashboardActivity");
                break;
            case "student":
            default:
                intent = new Intent(this, StudentDashboardActivity.class);
                android.util.Log.d("LoginActivity", "Redirecting to StudentDashboardActivity");
                break;
        }

        // Clear the activity stack so user can't go back to login
        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
        startActivity(intent);
        finish();
    }

    private boolean validateInput(String email, String password) {
        if (TextUtils.isEmpty(email)) {
            etEmail.setError("Vui lòng nhập email");
            etEmail.requestFocus();
            return false;
        }

        if (!android.util.Patterns.EMAIL_ADDRESS.matcher(email).matches()) {
            etEmail.setError("Email không hợp lệ");
            etEmail.requestFocus();
            return false;
        }

        if (TextUtils.isEmpty(password)) {
            etPassword.setError("Vui lòng nhập mật khẩu");
            etPassword.requestFocus();
            return false;
        }

        if (password.length() < 6) {
            etPassword.setError("Mật khẩu phải có ít nhất 6 ký tự");
            etPassword.requestFocus();
            return false;
        }

        return true;
    }
}

package com.vantinh.tienganh;

import android.content.Intent;
import android.os.Bundle;
import android.view.MenuItem;
import android.view.View;
import android.widget.LinearLayout;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.QueryDocumentSnapshot;
import java.util.ArrayList;
import java.util.List;

public class StudentMyCoursesActivity extends AppCompatActivity implements StudentEnrolledCourseAdapter.OnCourseClickListener {

    private RecyclerView rvMyCourses;
    private LinearLayout layoutNoCourses;
    private Toolbar toolbar;

    private FirebaseAuth mAuth;
    private FirebaseFirestore db;
    private List<EnrolledCourse> enrolledCourseList;
    private StudentEnrolledCourseAdapter courseAdapter;
    private String currentStudentId;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_student_my_courses);

        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();
        enrolledCourseList = new ArrayList<>();

        initViews();
        setupToolbar();
        setupRecyclerView();
        getCurrentStudentId();
    }

    private void initViews() {
        toolbar = findViewById(R.id.toolbar);
        rvMyCourses = findViewById(R.id.rv_my_courses);
        layoutNoCourses = findViewById(R.id.layout_no_courses);
    }

    private void setupToolbar() {
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Khóa học của tôi");
            getSupportActionBar().setDisplayHomeAsUpEnabled(true);
        }
    }

    private void setupRecyclerView() {
        courseAdapter = new StudentEnrolledCourseAdapter(enrolledCourseList, this);
        rvMyCourses.setLayoutManager(new LinearLayoutManager(this));
        rvMyCourses.setAdapter(courseAdapter);
    }

    private void getCurrentStudentId() {
        if (mAuth.getCurrentUser() == null) {
            Toast.makeText(this, "Vui lòng đăng nhập", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        String firebaseUid = mAuth.getCurrentUser().getUid();

        // Get student info từ users collection
        db.collection("users").document(firebaseUid)
                .get()
                .addOnSuccessListener(documentSnapshot -> {
                    if (documentSnapshot.exists()) {
                        String userRole = documentSnapshot.getString("role");
                        currentStudentId = documentSnapshot.getString("id");

                        if (!"student".equals(userRole)) {
                            Toast.makeText(this, "Chỉ học viên mới có thể xem khóa học của mình", Toast.LENGTH_SHORT).show();
                            finish();
                            return;
                        }

                        if (currentStudentId != null) {
                            loadEnrolledCourses();
                        } else {
                            Toast.makeText(this, "Không tìm thấy thông tin học viên", Toast.LENGTH_SHORT).show();
                            finish();
                        }
                    } else {
                        Toast.makeText(this, "Không tìm thấy thông tin người dùng", Toast.LENGTH_SHORT).show();
                        finish();
                    }
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("StudentMyCourses", "Error loading user info", e);
                    Toast.makeText(this, "Lỗi tải thông tin: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                    finish();
                });
    }

    private void loadEnrolledCourses() {
        layoutNoCourses.setVisibility(View.VISIBLE);
        rvMyCourses.setVisibility(View.GONE);

        android.util.Log.d("StudentMyCourses", "Loading enrolled courses for student: " + currentStudentId);

        // Load approved enrollments từ collection "enrollments"
        db.collection("enrollments")
                .whereEqualTo("studentId", currentStudentId)
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    enrolledCourseList.clear();

                    android.util.Log.d("StudentMyCourses", "Found " + queryDocumentSnapshots.size() + " enrollments");

                    if (queryDocumentSnapshots.isEmpty()) {
                        showEmptyState();
                        return;
                    }

                    // Với mỗi enrollment, load thông tin course tương ứng
                    for (QueryDocumentSnapshot enrollmentDoc : queryDocumentSnapshots) {
                        String courseId = enrollmentDoc.getString("courseId");
                        String courseName = enrollmentDoc.getString("courseName");
                        String enrollmentDate = enrollmentDoc.getTimestamp("enrollmentDate") != null ?
                            enrollmentDoc.getTimestamp("enrollmentDate").toDate().toString() : "";

                        if (courseId != null) {
                            loadCourseDetails(courseId, courseName, enrollmentDate, enrollmentDoc.getId());
                        }
                    }
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("StudentMyCourses", "Error loading enrollments", e);
                    Toast.makeText(this, "Lỗi tải khóa học: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                    showEmptyState();
                });
    }

    private void loadCourseDetails(String courseId, String courseName, String enrollmentDate, String enrollmentId) {
        db.collection("courses").document(courseId)
                .get()
                .addOnSuccessListener(courseDoc -> {
                    if (courseDoc.exists()) {
                        Course course = courseDoc.toObject(Course.class);
                        if (course != null) {
                            course.setId(courseDoc.getId());

                            // Tạo EnrolledCourse object
                            EnrolledCourse enrolledCourse = new EnrolledCourse();
                            enrolledCourse.setCourse(course);
                            enrolledCourse.setEnrollmentId(enrollmentId);
                            enrolledCourse.setEnrollmentDate(enrollmentDate);
<<<<<<< HEAD
                            enrolledCourse.setProgress(0); //
=======
                            enrolledCourse.setProgress(0); // TODO: Calculate actual progress
>>>>>>> 542cd6292b68990ee1f3d7aca232549a251d4232

                            enrolledCourseList.add(enrolledCourse);

                            // Update UI
                            updateUI();
                        }
                    }
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("StudentMyCourses", "Error loading course details for: " + courseId, e);
                });
    }

    private void updateUI() {
        if (enrolledCourseList.isEmpty()) {
            showEmptyState();
        } else {
            layoutNoCourses.setVisibility(View.GONE);
            rvMyCourses.setVisibility(View.VISIBLE);
            courseAdapter.notifyDataSetChanged();

            android.util.Log.d("StudentMyCourses", "Showing " + enrolledCourseList.size() + " enrolled courses");
        }
    }

    private void showEmptyState() {
        layoutNoCourses.setVisibility(View.VISIBLE);
        rvMyCourses.setVisibility(View.GONE);
    }

    @Override
    public void onCourseClick(EnrolledCourse enrolledCourse) {
        // Navigate to course learning activity
        Intent intent = new Intent(this, StudentCourseDetailActivity.class);
        intent.putExtra("courseId", enrolledCourse.getCourse().getId());
        intent.putExtra("courseTitle", enrolledCourse.getCourse().getTitle());
        intent.putExtra("courseCategory", enrolledCourse.getCourse().getCategory());
        intent.putExtra("enrollmentId", enrolledCourse.getEnrollmentId());
        startActivity(intent);
    }

    @Override
    public void onContinueLearning(EnrolledCourse enrolledCourse) {
        // Navigate to lessons list
        Intent intent = new Intent(this, StudentCourseLessonsActivity.class);
        intent.putExtra("courseId", enrolledCourse.getCourse().getId());
        intent.putExtra("courseTitle", enrolledCourse.getCourse().getTitle());
        intent.putExtra("courseCategory", enrolledCourse.getCourse().getCategory());
        startActivity(intent);
    }

    @Override
    public void onViewProgress(EnrolledCourse enrolledCourse) {
        // Navigate to progress view
        Intent intent = new Intent(this, StudentProgressDetailActivity.class);
        intent.putExtra("courseId", enrolledCourse.getCourse().getId());
        intent.putExtra("courseTitle", enrolledCourse.getCourse().getTitle());
        startActivity(intent);
    }

    @Override
    protected void onResume() {
        super.onResume();
        if (currentStudentId != null) {
            loadEnrolledCourses(); // Reload when returning to this activity
        }
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        if (item.getItemId() == android.R.id.home) {
            finish();
            return true;
        }
        return super.onOptionsItemSelected(item);
    }
}

package com.vantinh.tienganh;

import android.content.Intent;
import android.os.Bundle;
import android.view.MenuItem;
import android.view.View;
import android.widget.LinearLayout;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.QueryDocumentSnapshot;
import java.util.ArrayList;
import java.util.List;

public class StudentMyCoursesActivity extends AppCompatActivity implements StudentEnrolledCourseAdapter.OnCourseClickListener {

    private RecyclerView rvMyCourses;
    private LinearLayout layoutNoCourses;
    private Toolbar toolbar;

    private FirebaseAuth mAuth;
    private FirebaseFirestore db;
    private List<EnrolledCourse> enrolledCourseList;
    private StudentEnrolledCourseAdapter courseAdapter;
    private String currentStudentId;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_student_my_courses);

        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();
        enrolledCourseList = new ArrayList<>();

        initViews();
        setupToolbar();
        setupRecyclerView();
        getCurrentStudentId();
    }

    private void initViews() {
        toolbar = findViewById(R.id.toolbar);
        rvMyCourses = findViewById(R.id.rv_my_courses);
        layoutNoCourses = findViewById(R.id.layout_no_courses);
    }

    private void setupToolbar() {
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Khóa học của tôi");
            getSupportActionBar().setDisplayHomeAsUpEnabled(true);
        }
    }

    private void setupRecyclerView() {
        courseAdapter = new StudentEnrolledCourseAdapter(enrolledCourseList, this);
        rvMyCourses.setLayoutManager(new LinearLayoutManager(this));
        rvMyCourses.setAdapter(courseAdapter);
    }

    private void getCurrentStudentId() {
        if (mAuth.getCurrentUser() == null) {
            Toast.makeText(this, "Vui lòng đăng nhập", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        String firebaseUid = mAuth.getCurrentUser().getUid();

        // Get student info từ users collection
        db.collection("users").document(firebaseUid)
                .get()
                .addOnSuccessListener(documentSnapshot -> {
                    if (documentSnapshot.exists()) {
                        String userRole = documentSnapshot.getString("role");
                        currentStudentId = documentSnapshot.getString("id");

                        if (!"student".equals(userRole)) {
                            Toast.makeText(this, "Chỉ học viên mới có thể xem khóa học của mình", Toast.LENGTH_SHORT).show();
                            finish();
                            return;
                        }

                        if (currentStudentId != null) {
                            loadEnrolledCourses();
                        } else {
                            Toast.makeText(this, "Không tìm thấy thông tin học viên", Toast.LENGTH_SHORT).show();
                            finish();
                        }
                    } else {
                        Toast.makeText(this, "Không tìm thấy thông tin người dùng", Toast.LENGTH_SHORT).show();
                        finish();
                    }
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("StudentMyCourses", "Error loading user info", e);
                    Toast.makeText(this, "Lỗi tải thông tin: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                    finish();
                });
    }

    private void loadEnrolledCourses() {
        layoutNoCourses.setVisibility(View.VISIBLE);
        rvMyCourses.setVisibility(View.GONE);

        android.util.Log.d("StudentMyCourses", "Loading enrolled courses for student: " + currentStudentId);

        // Load approved enrollments từ collection "enrollments"
        db.collection("enrollments")
                .whereEqualTo("studentId", currentStudentId)
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    enrolledCourseList.clear();

                    android.util.Log.d("StudentMyCourses", "Found " + queryDocumentSnapshots.size() + " enrollments");

                    if (queryDocumentSnapshots.isEmpty()) {
                        showEmptyState();
                        return;
                    }

                    // Với mỗi enrollment, load thông tin course tương ứng
                    for (QueryDocumentSnapshot enrollmentDoc : queryDocumentSnapshots) {
                        String courseId = enrollmentDoc.getString("courseId");
                        String courseName = enrollmentDoc.getString("courseName");
                        String enrollmentDate = enrollmentDoc.getTimestamp("enrollmentDate") != null ?
                            enrollmentDoc.getTimestamp("enrollmentDate").toDate().toString() : "";

                        if (courseId != null) {
                            loadCourseDetails(courseId, courseName, enrollmentDate, enrollmentDoc.getId());
                        }
                    }
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("StudentMyCourses", "Error loading enrollments", e);
                    Toast.makeText(this, "Lỗi tải khóa học: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                    showEmptyState();
                });
    }

    private void loadCourseDetails(String courseId, String courseName, String enrollmentDate, String enrollmentId) {
        db.collection("courses").document(courseId)
                .get()
                .addOnSuccessListener(courseDoc -> {
                    if (courseDoc.exists()) {
                        Course course = courseDoc.toObject(Course.class);
                        if (course != null) {
                            course.setId(courseDoc.getId());

                            // Tạo EnrolledCourse object
                            EnrolledCourse enrolledCourse = new EnrolledCourse();
                            enrolledCourse.setCourse(course);
                            enrolledCourse.setEnrollmentId(enrollmentId);
                            enrolledCourse.setEnrollmentDate(enrollmentDate);
                            enrolledCourse.setProgress(0); // TODO: Calculate actual progress

                            enrolledCourseList.add(enrolledCourse);

                            // Update UI
                            updateUI();
                        }
                    }
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("StudentMyCourses", "Error loading course details for: " + courseId, e);
                });
    }

    private void updateUI() {
        if (enrolledCourseList.isEmpty()) {
            showEmptyState();
        } else {
            layoutNoCourses.setVisibility(View.GONE);
            rvMyCourses.setVisibility(View.VISIBLE);
            courseAdapter.notifyDataSetChanged();

            android.util.Log.d("StudentMyCourses", "Showing " + enrolledCourseList.size() + " enrolled courses");
        }
    }

    private void showEmptyState() {
        layoutNoCourses.setVisibility(View.VISIBLE);
        rvMyCourses.setVisibility(View.GONE);
    }

    @Override
    public void onCourseClick(EnrolledCourse enrolledCourse) {
        // Navigate to course learning activity
        Intent intent = new Intent(this, StudentCourseDetailActivity.class);
        intent.putExtra("courseId", enrolledCourse.getCourse().getId());
        intent.putExtra("courseTitle", enrolledCourse.getCourse().getTitle());
        intent.putExtra("courseCategory", enrolledCourse.getCourse().getCategory());
        intent.putExtra("enrollmentId", enrolledCourse.getEnrollmentId());
        startActivity(intent);
    }

    @Override
    public void onContinueLearning(EnrolledCourse enrolledCourse) {
        // Navigate to lessons list
        Intent intent = new Intent(this, StudentCourseLessonsActivity.class);
        intent.putExtra("courseId", enrolledCourse.getCourse().getId());
        intent.putExtra("courseTitle", enrolledCourse.getCourse().getTitle());
        intent.putExtra("courseCategory", enrolledCourse.getCourse().getCategory());
        startActivity(intent);
    }

    @Override
    public void onViewProgress(EnrolledCourse enrolledCourse) {
        // Navigate to progress view
        Intent intent = new Intent(this, StudentProgressDetailActivity.class);
        intent.putExtra("courseId", enrolledCourse.getCourse().getId());
        intent.putExtra("courseTitle", enrolledCourse.getCourse().getTitle());
        startActivity(intent);
    }

    @Override
    protected void onResume() {
        super.onResume();
        if (currentStudentId != null) {
            loadEnrolledCourses(); // Reload when returning to this activity
        }
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        if (item.getItemId() == android.R.id.home) {
            finish();
            return true;
        }
        return super.onOptionsItemSelected(item);
    }
}

package com.vantinh.tienganh;

import android.os.Bundle;
import android.view.MenuItem;
import android.widget.TextView;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.QueryDocumentSnapshot;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class StudentProgressDetailActivity extends AppCompatActivity {

    private TextView tvCourseTitle, tvProgressSummary;
    private RecyclerView rvLessonProgress;
    private Toolbar toolbar;

    private FirebaseAuth mAuth;
    private FirebaseFirestore db;
    private String courseId, courseTitle;
    private List<LessonProgressItem> lessonProgressList;
    private LessonProgressAdapter progressAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        // Use simple layout since we don't have custom layout
        setContentView(android.R.layout.activity_list_item);

        // Get course info from intent
        courseId = getIntent().getStringExtra("courseId");
        courseTitle = getIntent().getStringExtra("courseTitle");

        if (courseId == null) {
            Toast.makeText(this, "Lỗi: Không tìm thấy thông tin khóa học", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();

        initViews();
        setupRecyclerView();
        loadProgressData();
    }

    private void initViews() {
        // Use existing views from the layout that already exists
        try {
            toolbar = findViewById(R.id.toolbar);
            if (toolbar != null) {
                setSupportActionBar(toolbar);
                if (getSupportActionBar() != null) {
                    getSupportActionBar().setTitle("Chi tiết tiến độ học tập");
                    getSupportActionBar().setDisplayHomeAsUpEnabled(true);
                }
            }
        } catch (Exception e) {
            // If toolbar doesn't exist, set title directly
            setTitle("Chi tiết tiến độ học tập");
        }

        // Try to find existing views, if not found, show Toast messages instead
        try {
            tvCourseTitle = findViewById(R.id.tv_course_title);
        } catch (Exception e) {
            android.util.Log.d("StudentProgressDetail", "Course title view not found");
        }

        try {
            rvLessonProgress = findViewById(R.id.rv_lesson_progress);
        } catch (Exception e) {
            // Try to find any RecyclerView or ListView
            rvLessonProgress = findViewById(android.R.id.list);
        }

        // Create a simple TextView for progress summary if needed
        if (tvCourseTitle == null) {
            tvProgressSummary = new TextView(this);
            tvProgressSummary.setPadding(16, 16, 16, 16);
            tvProgressSummary.setTextSize(16);
        }
    }

    private void setupRecyclerView() {
        if (rvLessonProgress != null) {
            lessonProgressList = new ArrayList<>();
            progressAdapter = new LessonProgressAdapter(lessonProgressList);
            rvLessonProgress.setLayoutManager(new LinearLayoutManager(this));
            rvLessonProgress.setAdapter(progressAdapter);
        }
    }

    private void loadProgressData() {
        if (mAuth.getCurrentUser() == null) {
            Toast.makeText(this, "Vui lòng đăng nhập", Toast.LENGTH_SHORT).show();
            finish();
            return;
        }

        String studentId = mAuth.getCurrentUser().getUid();

        if (tvCourseTitle != null) {
            tvCourseTitle.setText(courseTitle);
        }

        // Load all lessons for this course
        db.collection("lessons")
                .whereEqualTo("courseId", courseId)
                .whereEqualTo("isPublished", true)
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    List<Lesson> allLessons = new ArrayList<>();
                    for (QueryDocumentSnapshot document : queryDocumentSnapshots) {
                        Lesson lesson = document.toObject(Lesson.class);
                        lesson.setId(document.getId());
                        allLessons.add(lesson);
                    }

                    if (allLessons.isEmpty()) {
                        String message = "Khóa học này chưa có bài học nào";
                        if (tvProgressSummary != null) {
                            tvProgressSummary.setText(message);
                        } else {
                            Toast.makeText(this, message, Toast.LENGTH_LONG).show();
                        }
                        return;
                    }

                    // Load progress for each lesson
                    loadLessonProgress(studentId, allLessons);
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("StudentProgressDetail", "Error loading lessons", e);
                    Toast.makeText(this, "Lỗi tải danh sách bài học: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                });
    }

    private void loadLessonProgress(String studentId, List<Lesson> allLessons) {
        // Load all progress records for this student and course
        db.collection("lesson_progress")
                .whereEqualTo("studentId", studentId)
                .whereEqualTo("courseId", courseId)
                .whereEqualTo("isCompleted", true)
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    // Create a map of completed lesson IDs
                    Map<String, Boolean> completedLessons = new HashMap<>();
                    for (QueryDocumentSnapshot document : queryDocumentSnapshots) {
                        String lessonId = document.getString("lessonId");
                        if (lessonId != null) {
                            completedLessons.put(lessonId, true);
                        }
                    }

                    // Create progress items for all lessons
                    if (lessonProgressList != null) {
                        lessonProgressList.clear();
                    } else {
                        lessonProgressList = new ArrayList<>();
                    }

                    int completedCount = 0;

                    for (Lesson lesson : allLessons) {
                        boolean isCompleted = completedLessons.containsKey(lesson.getId());
                        if (isCompleted) {
                            completedCount++;
                        }

                        LessonProgressItem progressItem = new LessonProgressItem(
                                lesson.getId(),
                                lesson.getTitle(),
                                lesson.getTypeDisplayName(),
                                isCompleted
                        );
                        lessonProgressList.add(progressItem);
                    }

                    // Update UI
                    int totalLessons = allLessons.size();
                    int progressPercentage = totalLessons > 0 ? (completedCount * 100) / totalLessons : 0;

                    String progressText = "Bạn đã hoàn thành " + completedCount + " trong tổng số " + totalLessons + " bài học";
                    String completionText = progressPercentage + "% hoàn thành (" + completedCount + "/" + totalLessons + ")";

                    if (tvProgressSummary != null) {
                        tvProgressSummary.setText(progressText + "\n" + completionText);
                    }

                    // Show progress as Toast if no UI elements available
                    Toast.makeText(this, completionText, Toast.LENGTH_LONG).show();

                    if (progressAdapter != null) {
                        progressAdapter.notifyDataSetChanged();
                    }

                    android.util.Log.d("StudentProgressDetail", "Progress loaded: " + completedCount + "/" + totalLessons);
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("StudentProgressDetail", "Error loading lesson progress", e);
                    Toast.makeText(this, "Lỗi tải tiến độ học tập: " + e.getMessage(), Toast.LENGTH_SHORT).show();
                });
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        if (item.getItemId() == android.R.id.home) {
            finish();
            return true;
        }
        return super.onOptionsItemSelected(item);
    }

    // Inner class for lesson progress item
    public static class LessonProgressItem {
        private String lessonId;
        private String lessonTitle;
        private String lessonType;
        private boolean isCompleted;

        public LessonProgressItem(String lessonId, String lessonTitle, String lessonType, boolean isCompleted) {
            this.lessonId = lessonId;
            this.lessonTitle = lessonTitle;
            this.lessonType = lessonType;
            this.isCompleted = isCompleted;
        }

        // Getters
        public String getLessonId() { return lessonId; }
        public String getLessonTitle() { return lessonTitle; }
        public String getLessonType() { return lessonType; }
        public boolean isCompleted() { return isCompleted; }
    }
}

