package com.vantinh.tienganh;

import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.view.Menu;
import android.view.MenuItem;
import android.widget.Button;
import android.widget.TextView;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.vantinh.tienganh.utils.RealtimeManager;

public class TeacherDashboardActivity extends AppCompatActivity {

    private Toolbar toolbar;
    private TextView tvWelcome, tvCoursesCount, tvStudentsCount, tvPendingRequestsCount;
    private Button btnManageCourses, btnViewRequests, btnCreateQuiz, btnCreateCourse;
    private FirebaseAuth mAuth;
    private FirebaseFirestore db;
    private RealtimeManager realtimeManager;
    private String currentTeacherId; // Đổi từ currentTeacherName sang currentTeacherId

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_teacher_dashboard);

        initViews();
        setupToolbar();
        initFirebase();
        loadDashboardData();
        setupClickListeners();
    }

    private void initViews() {
        toolbar = findViewById(R.id.toolbar);
        tvWelcome = findViewById(R.id.tv_welcome);
        tvCoursesCount = findViewById(R.id.tv_courses_count);
        tvStudentsCount = findViewById(R.id.tv_students_count);
        tvPendingRequestsCount = findViewById(R.id.tv_pending_requests_count);

        btnManageCourses = findViewById(R.id.btn_manage_courses);
        btnViewRequests = findViewById(R.id.btn_view_requests);
        btnCreateQuiz = findViewById(R.id.btn_create_quiz);
        btnCreateCourse = findViewById(R.id.btn_create_course);
    }

    private void setupToolbar() {
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Bảng điều khiển giáo viên");
        }
    }

    private void initFirebase() {
        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();
        realtimeManager = RealtimeManager.getInstance();
    }

    private void setupClickListeners() {
        btnManageCourses.setOnClickListener(v -> {
            startActivity(new Intent(this, CourseManagementActivity.class));
        });

        // Thêm click listener cho nút "View Requests"
        btnViewRequests.setOnClickListener(v -> {
            startActivity(new Intent(this, CourseRequestManagementActivity.class));
        });

        btnCreateQuiz.setOnClickListener(v -> {
            startActivity(new Intent(this, SelectCourseForQuizActivity.class));
        });

        // Nút "Quản lý đăng ký" - chuyển đến EnrollmentManagementActivity
        btnCreateCourse.setOnClickListener(v -> {
            Intent intent = new Intent(this, EnrollmentManagementActivity.class);
            // Truyền teacherId để activity có thể lọc dữ liệu theo giáo viên
            intent.putExtra("teacherId", currentTeacherId);
            startActivity(intent);
        });
    }

    private void loadDashboardData() {
        String teacherId = mAuth.getCurrentUser().getUid();
        currentTeacherId = teacherId; // Lưu teacherId trực tiếp

        // Load teacher info
        db.collection("users").document(teacherId)
                .get()
                .addOnSuccessListener(documentSnapshot -> {
                    if (documentSnapshot.exists()) {
                        String fullName = documentSnapshot.getString("fullName");
                        tvWelcome.setText("Xin chào, " + fullName);

                        // Load counts với teacherId
                        loadCoursesCount(teacherId);
                        loadStudentsCount(teacherId);
                        setupRealTimePendingRequestsCount(); // Sử dụng teacherId
                    }
                })
                .addOnFailureListener(e -> {
                    Toast.makeText(this, "Lỗi khi tải thông tin", Toast.LENGTH_SHORT).show();
                });
    }

    private void loadCoursesCount(String teacherId) {
        db.collection("courses")
                .whereEqualTo("teacherId", teacherId)
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    int count = queryDocumentSnapshots.size();
                    tvCoursesCount.setText(String.valueOf(count));
                })
                .addOnFailureListener(e -> {
                    tvCoursesCount.setText("0");
                });
    }

    private void loadStudentsCount(String teacherId) {
        Log.d("TeacherDashboard", "Starting loadStudentsCount for teacherId: " + teacherId);

        // Kiểm tra tất cả enrollments trong database trước
        db.collection("enrollments")
                .get()
                .addOnSuccessListener(allEnrollments -> {
                    Log.d("TeacherDashboard", "Total enrollments in database: " + allEnrollments.size());
                    for (com.google.firebase.firestore.QueryDocumentSnapshot doc : allEnrollments) {
                        String courseID = doc.getString("courseID");
                        String studentID = doc.getString("studentID");
                        String fullName = doc.getString("fullName");
                        Log.d("TeacherDashboard", "Enrollment found - CourseID: " + courseID + ", StudentID: " + studentID + ", Name: " + fullName);
                    }
                });

        // Lấy tất cả courses của teacher trước
        db.collection("courses")
                .whereEqualTo("teacherId", teacherId)
                .get()
                .addOnSuccessListener(courseSnapshots -> {
                    Log.d("TeacherDashboard", "Found " + courseSnapshots.size() + " courses for teacher");

                    if (courseSnapshots.isEmpty()) {
                        Log.d("TeacherDashboard", "No courses found, setting students count to 0");
                        tvStudentsCount.setText("0");
                        return;
                    }

                    // Tạo Set để lưu unique studentID
                    java.util.Set<String> uniqueStudentIds = new java.util.HashSet<>();
                    java.util.concurrent.atomic.AtomicInteger coursesProcessed = new java.util.concurrent.atomic.AtomicInteger(0);
                    int totalCourses = courseSnapshots.size();

                    // Duyệt qua từng khóa học của teacher
                    for (com.google.firebase.firestore.QueryDocumentSnapshot courseDoc : courseSnapshots) {
                        String courseId = courseDoc.getId();
                        String courseName = courseDoc.getString("courseName");
                        Log.d("TeacherDashboard", "Processing course: " + courseName + " (ID: " + courseId + ")");

                        // Lấy enrollments cho khóa học này
                        db.collection("enrollments")
                                .whereEqualTo("courseID", courseId)
                                .get()
                                .addOnSuccessListener(enrollmentSnapshots -> {
                                    Log.d("TeacherDashboard", "Found " + enrollmentSnapshots.size() + " enrollments for course: " + courseId);

                                    // Thêm studentID vào Set (tự động loại bỏ trùng lặp)
                                    for (com.google.firebase.firestore.QueryDocumentSnapshot enrollmentDoc : enrollmentSnapshots) {
                                        String studentId = enrollmentDoc.getString("studentID");
                                        String studentName = enrollmentDoc.getString("fullName");
                                        Log.d("TeacherDashboard", "Processing enrollment - StudentID: " + studentId + ", Name: " + studentName);

                                        if (studentId != null && !studentId.isEmpty()) {
                                            uniqueStudentIds.add(studentId);
                                            Log.d("TeacherDashboard", "Added studentID: " + studentId + ". Total unique students: " + uniqueStudentIds.size());
                                        }
                                    }

                                    // Kiểm tra xem đã xử lý hết tất cả courses chưa
                                    if (coursesProcessed.incrementAndGet() >= totalCourses) {
                                        Log.d("TeacherDashboard", "Finished processing all courses. Final unique students count: " + uniqueStudentIds.size());
                                        // Cập nhật UI với số lượng học viên unique
                                        runOnUiThread(() -> {
                                            tvStudentsCount.setText(String.valueOf(uniqueStudentIds.size()));
                                        });
                                    }
                                })
                                .addOnFailureListener(e -> {
                                    Log.e("TeacherDashboard", "Error loading enrollments for course: " + courseId, e);
                                    if (coursesProcessed.incrementAndGet() >= totalCourses) {
                                        Log.d("TeacherDashboard", "Finished processing all courses (with some errors). Final unique students count: " + uniqueStudentIds.size());
                                        runOnUiThread(() -> {
                                            tvStudentsCount.setText(String.valueOf(uniqueStudentIds.size()));
                                        });
                                    }
                                });
                    }
                })
                .addOnFailureListener(e -> {
                    Log.e("TeacherDashboard", "Error loading courses", e);
                    tvStudentsCount.setText("0");
                });
    }

    // Sửa để hiển thị TẤT CẢ pending requests (không filter theo teacherId)
    private void setupRealTimePendingRequestsCount() {
        // Đơn giản hóa - đếm tất cả pending requests
        db.collection("courseRequests")
                .whereEqualTo("status", "pending")
                .addSnapshotListener((queryDocumentSnapshots, e) -> {
                    if (e != null) {
                        Log.e("TeacherDashboard", "Error listening to pending requests", e);
                        tvPendingRequestsCount.setText("0");
                        return;
                    }

                    if (queryDocumentSnapshots != null) {
                        int count = queryDocumentSnapshots.size();
                        Log.d("TeacherDashboard", "Real-time update: " + count + " pending requests total");

                        runOnUiThread(() -> {
                            tvPendingRequestsCount.setText(String.valueOf(count));

                            if (count > 0) {
                                btnViewRequests.setText("Xem yêu cầu (" + count + ")");
                                // Animation nhấp nháy khi có yêu cầu mới
                                btnViewRequests.animate()
                                        .scaleX(1.1f)
                                        .scaleY(1.1f)
                                        .setDuration(200)
                                        .withEndAction(() -> {
                                            btnViewRequests.animate()
                                                    .scaleX(1f)
                                                    .scaleY(1f)
                                                    .setDuration(200)
                                                    .start();
                                        })
                                        .start();
                            } else {
                                btnViewRequests.setText("Xem yêu cầu");
                            }
                        });
                    }
                });
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.teacher_toolbar_menu, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        int itemId = item.getItemId();
        if (itemId == R.id.action_logout) {
            logout();
            return true;
        }
        return super.onOptionsItemSelected(item);
    }

    private void logout() {
        mAuth.signOut();
        Intent intent = new Intent(this, LoginActivity.class);
        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
        startActivity(intent);
        finish();
    }

    @Override
    protected void onResume() {
        super.onResume();
        // Refresh data khi quay lại activity
        loadDashboardData();
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
        // Cleanup listeners khi destroy activity
        if (realtimeManager != null) {
            realtimeManager.removeAllListeners();
        }
    }
}

package com.vantinh.tienganh;

import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.view.Menu;
import android.view.MenuItem;
import android.widget.Button;
import android.widget.TextView;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;
import com.vantinh.tienganh.utils.RealtimeManager;

public class TeacherDashboardActivity extends AppCompatActivity {

    private Toolbar toolbar;
    private TextView tvWelcome, tvCoursesCount, tvStudentsCount, tvPendingRequestsCount;
    private Button btnManageCourses, btnViewRequests, btnCreateQuiz, btnCreateCourse;
    private FirebaseAuth mAuth;
    private FirebaseFirestore db;
    private RealtimeManager realtimeManager;
    private String currentTeacherId; // Đổi từ currentTeacherName sang currentTeacherId

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_teacher_dashboard);

        initViews();
        setupToolbar();
        initFirebase();
        loadDashboardData();
        setupClickListeners();
    }

    private void initViews() {
        toolbar = findViewById(R.id.toolbar);
        tvWelcome = findViewById(R.id.tv_welcome);
        tvCoursesCount = findViewById(R.id.tv_courses_count);
        tvStudentsCount = findViewById(R.id.tv_students_count);
        tvPendingRequestsCount = findViewById(R.id.tv_pending_requests_count);

        btnManageCourses = findViewById(R.id.btn_manage_courses);
        btnViewRequests = findViewById(R.id.btn_view_requests);
        btnCreateQuiz = findViewById(R.id.btn_create_quiz);
        btnCreateCourse = findViewById(R.id.btn_create_course);
    }

    private void setupToolbar() {
        setSupportActionBar(toolbar);
        if (getSupportActionBar() != null) {
            getSupportActionBar().setTitle("Bảng điều khiển giáo viên");
        }
    }

    private void initFirebase() {
        mAuth = FirebaseAuth.getInstance();
        db = FirebaseFirestore.getInstance();
        realtimeManager = RealtimeManager.getInstance();
    }

    private void setupClickListeners() {
        btnManageCourses.setOnClickListener(v -> {
            startActivity(new Intent(this, CourseManagementActivity.class));
        });

        // Thêm click listener cho nút "View Requests"
        btnViewRequests.setOnClickListener(v -> {
            startActivity(new Intent(this, CourseRequestManagementActivity.class));
        });

        btnCreateQuiz.setOnClickListener(v -> {
            startActivity(new Intent(this, SelectCourseForQuizActivity.class));
        });

        // Nút "Quản lý đăng ký" - chuyển đến EnrollmentManagementActivity
        btnCreateCourse.setOnClickListener(v -> {
            Intent intent = new Intent(this, EnrollmentManagementActivity.class);
            // Truyền teacherId để activity có thể lọc dữ liệu theo giáo viên
            intent.putExtra("teacherId", currentTeacherId);
            startActivity(intent);
        });
    }

    private void loadDashboardData() {
        String teacherId = mAuth.getCurrentUser().getUid();
        currentTeacherId = teacherId; // Lưu teacherId trực tiếp

        // Thêm debug enrollments
        debugEnrollments();

        // Load teacher info
        db.collection("users").document(teacherId)
                .get()
                .addOnSuccessListener(documentSnapshot -> {
                    if (documentSnapshot.exists()) {
                        String fullName = documentSnapshot.getString("fullName");
                        tvWelcome.setText("Xin chào, " + fullName);

                        // Load counts với teacherId
                        loadCoursesCount(teacherId);
                        loadStudentsCount(teacherId);
                        setupRealTimePendingRequestsCount(); // Sử dụng teacherId
                    }
                })
                .addOnFailureListener(e -> {
                    Toast.makeText(this, "Lỗi khi tải thông tin", Toast.LENGTH_SHORT).show();
                });
    }

    private void loadCoursesCount(String teacherId) {
        db.collection("courses")
                .whereEqualTo("teacherId", teacherId)
                .get()
                .addOnSuccessListener(queryDocumentSnapshots -> {
                    int count = queryDocumentSnapshots.size();
                    tvCoursesCount.setText(String.valueOf(count));
                })
                .addOnFailureListener(e -> {
                    tvCoursesCount.setText("0");
                });
    }

    private void loadStudentsCount(String teacherId) {
        Log.d("TeacherDashboard", "Starting loadStudentsCount for teacherId: " + teacherId);

        // Lấy tất cả approved requests từ courseRequests
        db.collection("courseRequests")
                .whereEqualTo("status", "approved")
                .get()
                .addOnSuccessListener(approvedRequests -> {
                    Log.d("TeacherDashboard", "Found " + approvedRequests.size() + " approved requests total");

                    if (approvedRequests.isEmpty()) {
                        Log.d("TeacherDashboard", "No approved requests found, setting students count to 0");
                        tvStudentsCount.setText("0");
                        return;
                    }

                    // Lấy danh sách courseIds của teacher này
                    db.collection("courses")
                            .whereEqualTo("teacherId", teacherId)
                            .get()
                            .addOnSuccessListener(teacherCourses -> {
                                Log.d("TeacherDashboard", "Teacher has " + teacherCourses.size() + " courses");

                                // Tạo Set chứa courseIds của teacher
                                java.util.Set<String> teacherCourseIds = new java.util.HashSet<>();
                                for (com.google.firebase.firestore.QueryDocumentSnapshot courseDoc : teacherCourses) {
                                    teacherCourseIds.add(courseDoc.getId());
                                    Log.d("TeacherDashboard", "Teacher course ID: " + courseDoc.getId());
                                }

                                // Tạo Set để lưu unique studentId của teacher này
                                java.util.Set<String> uniqueStudentIds = new java.util.HashSet<>();

                                // Duyệt qua tất cả approved requests
                                for (com.google.firebase.firestore.QueryDocumentSnapshot requestDoc : approvedRequests) {
                                    String studentName = requestDoc.getString("studentName");
                                    String studentId = requestDoc.getString("studentId");
                                    String courseId = requestDoc.getString("courseId");
                                    String courseName = requestDoc.getString("courseName");

                                    Log.d("TeacherDashboard", "Processing approved request - Student: " + studentName +
                                            ", StudentId: " + studentId + ", CourseId: " + courseId + ", Course: " + courseName);

                                    // Chỉ đếm nếu courseId thuộc về teacher này
                                    if (teacherCourseIds.contains(courseId)) {
                                        if (studentId != null && !studentId.isEmpty()) {
                                            uniqueStudentIds.add(studentId);
                                            Log.d("TeacherDashboard", "Added studentId: " + studentId +
                                                    " for teacher's course: " + courseName + ". Total unique students: " + uniqueStudentIds.size());
                                        }
                                    } else {
                                        Log.d("TeacherDashboard", "Skipping request for course not belonging to this teacher: " + courseName);
                                    }
                                }

                                // Cập nhật UI
                                int finalCount = uniqueStudentIds.size();
                                Log.d("TeacherDashboard", "Final unique students count for teacher: " + finalCount);

                                runOnUiThread(() -> {
                                    tvStudentsCount.setText(String.valueOf(finalCount));
                                });
                            })
                            .addOnFailureListener(e -> {
                                Log.e("TeacherDashboard", "Error loading teacher courses", e);
                                tvStudentsCount.setText("0");
                            });
                })
                .addOnFailureListener(e -> {
                    Log.e("TeacherDashboard", "Error loading approved requests", e);
                    tvStudentsCount.setText("0");
                });
    }

    // Sửa để hiển thị TẤT CẢ pending requests (không filter theo teacherId)
    private void setupRealTimePendingRequestsCount() {
        // Đơn giản hóa - đếm tất cả pending requests
        db.collection("courseRequests")
                .whereEqualTo("status", "pending")
                .addSnapshotListener((queryDocumentSnapshots, e) -> {
                    if (e != null) {
                        Log.e("TeacherDashboard", "Error listening to pending requests", e);
                        tvPendingRequestsCount.setText("0");
                        return;
                    }

                    if (queryDocumentSnapshots != null) {
                        int count = queryDocumentSnapshots.size();
                        Log.d("TeacherDashboard", "Real-time update: " + count + " pending requests total");

                        runOnUiThread(() -> {
                            tvPendingRequestsCount.setText(String.valueOf(count));

                            if (count > 0) {
                                btnViewRequests.setText("Xem yêu cầu (" + count + ")");
                                // Animation nhấp nháy khi có yêu cầu mới
                                btnViewRequests.animate()
                                        .scaleX(1.1f)
                                        .scaleY(1.1f)
                                        .setDuration(200)
                                        .withEndAction(() -> {
                                            btnViewRequests.animate()
                                                    .scaleX(1f)
                                                    .scaleY(1f)
                                                    .setDuration(200)
                                                    .start();
                                        })
                                        .start();
                            } else {
                                btnViewRequests.setText("Xem yêu cầu");
                            }
                        });
                    }
                });
    }

    // Thêm method debug để kiểm tra enrollments
    private void debugEnrollments() {
        Log.d("TeacherDashboard", "=== DEBUG: Checking all enrollments ===");

        db.collection("enrollments")
                .get()
                .addOnSuccessListener(snapshots -> {
                    Log.d("TeacherDashboard", "Total enrollments in database: " + snapshots.size());

                    if (snapshots.isEmpty()) {
                        Log.d("TeacherDashboard", "❌ NO ENROLLMENTS FOUND - This is why student count is 0");
                        return;
                    }

                    for (com.google.firebase.firestore.QueryDocumentSnapshot doc : snapshots) {
                        String courseID = doc.getString("courseID");
                        String studentID = doc.getString("studentID");
                        String fullName = doc.getString("fullName");

                        Log.d("TeacherDashboard", "Enrollment: " + fullName + " → Course: " + courseID + " (StudentID: " + studentID + ")");
                    }
                })
                .addOnFailureListener(e -> {
                    Log.e("TeacherDashboard", "Error checking enrollments", e);
                });
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.teacher_toolbar_menu, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(@NonNull MenuItem item) {
        int itemId = item.getItemId();
        if (itemId == R.id.action_logout) {
            logout();
            return true;
        }
        return super.onOptionsItemSelected(item);
    }

    private void logout() {
        mAuth.signOut();
        Intent intent = new Intent(this, LoginActivity.class);
        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
        startActivity(intent);
        finish();
    }

    @Override
    protected void onResume() {
        super.onResume();
        // Refresh data khi quay lại activity
        loadDashboardData();
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();
        // Cleanup listeners khi destroy activity
        if (realtimeManager != null) {
            realtimeManager.removeAllListeners();
        }
    }
}

package com.vantinh.tienganh.adapters;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import com.vantinh.tienganh.R;
import com.vantinh.tienganh.models.Course;
import java.util.List;

public class CourseSelectAdapter extends RecyclerView.Adapter<CourseSelectAdapter.ViewHolder> {

    private List<Course> courses;
    private OnCourseSelectListener listener;

    public interface OnCourseSelectListener {
        void onCourseSelected(Course course);
    }

    public CourseSelectAdapter(List<Course> courses, OnCourseSelectListener listener) {
        this.courses = courses;
        this.listener = listener;
    }

    @NonNull
    @Override
    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_course_select, parent, false);
        return new ViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
        Course course = courses.get(position);
        holder.bind(course);
    }

    @Override
    public int getItemCount() {
        return courses.size();
    }

    class ViewHolder extends RecyclerView.ViewHolder {
        private TextView tvCourseName, tvCourseDescription;

        public ViewHolder(@NonNull View itemView) {
            super(itemView);
            tvCourseName = itemView.findViewById(R.id.tv_course_name);
            tvCourseDescription = itemView.findViewById(R.id.tv_course_description);

            itemView.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onCourseSelected(courses.get(getAdapterPosition()));
                }
            });
        }

        public void bind(Course course) {
            tvCourseName.setText(course.getCourseName());
            tvCourseDescription.setText(course.getDescription());
        }
    }
}

package com.vantinh.tienganh.adapters;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import com.vantinh.tienganh.R;
import com.vantinh.tienganh.models.SimpleTestQuestion;
import java.util.List;

public class EditTestQuestionAdapter extends RecyclerView.Adapter<EditTestQuestionAdapter.ViewHolder> {

    private List<SimpleTestQuestion> questions;
    private OnQuestionClickListener editListener;
    private OnQuestionClickListener deleteListener;

    public interface OnQuestionClickListener {
        void onQuestionClick(SimpleTestQuestion question);
    }

    public EditTestQuestionAdapter(List<SimpleTestQuestion> questions, 
                                  OnQuestionClickListener editListener,
                                  OnQuestionClickListener deleteListener) {
        this.questions = questions;
        this.editListener = editListener;
        this.deleteListener = deleteListener;
    }

    @NonNull
    @Override
    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_edit_test_question, parent, false);
        return new ViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
        SimpleTestQuestion question = questions.get(position);

        holder.tvQuestionNumber.setText("Câu " + (position + 1));
        holder.tvQuestion.setText(question.getQuestion());

        // Hiển thị các lựa chọn
        List<String> options = question.getCorrectAnswer();
        if (options != null && options.size() >= 4) {
            holder.tvOptionA.setText("A. " + options.get(0));
            holder.tvOptionB.setText("B. " + options.get(1));
            holder.tvOptionC.setText("C. " + options.get(2));
            holder.tvOptionD.setText("D. " + options.get(3));
        }

        // Hiển thị đáp án đúng
        int correctIndex = question.getOptions();
        String correctLabel = "";
        switch (correctIndex) {
            case 0: correctLabel = "A"; break;
            case 1: correctLabel = "B"; break;
            case 2: correctLabel = "C"; break;
            case 3: correctLabel = "D"; break;
        }
        holder.tvCorrectAnswer.setText("Đáp án đúng: " + correctLabel);

        // Set click listeners
        holder.btnEdit.setOnClickListener(v -> {
            if (editListener != null) {
                editListener.onQuestionClick(question);
            }
        });

        holder.btnDelete.setOnClickListener(v -> {
            if (deleteListener != null) {
                deleteListener.onQuestionClick(question);
            }
        });
    }

    @Override
    public int getItemCount() {
        return questions.size();
    }

    public static class ViewHolder extends RecyclerView.ViewHolder {
        TextView tvQuestionNumber, tvQuestion, tvOptionA, tvOptionB, tvOptionC, tvOptionD, tvCorrectAnswer;
        Button btnEdit, btnDelete;

        public ViewHolder(@NonNull View itemView) {
            super(itemView);
            tvQuestionNumber = itemView.findViewById(R.id.tv_question_number);
            tvQuestion = itemView.findViewById(R.id.tv_question);
            tvOptionA = itemView.findViewById(R.id.tv_option_a);
            tvOptionB = itemView.findViewById(R.id.tv_option_b);
            tvOptionC = itemView.findViewById(R.id.tv_option_c);
            tvOptionD = itemView.findViewById(R.id.tv_option_d);
            tvCorrectAnswer = itemView.findViewById(R.id.tv_correct_answer);
            btnEdit = itemView.findViewById(R.id.btn_edit);
            btnDelete = itemView.findViewById(R.id.btn_delete);
        }
    }
}

package com.vantinh.tienganh.models;

import java.util.Date;
import java.util.List;

public class Course {
    private String id;
    private String title;
    private String description;
    private String teacherId;
    private String imageUrl;
    private String level; // Beginner, Intermediate, Advanced
    private String category; // Grammar, Vocabulary, Listening, Speaking
    private int duration; // in hours
    private Date createdAt;
    private Date updatedAt;
    private boolean isActive;
    private int enrolledStudents;
    private double rating;
    private List<String> tags;

    public Course() {
        // Required empty constructor for Firebase
    }

    public Course(String title, String description, String teacherId,
                  String level, String category, int duration) {
        this.title = title;
        this.description = description;
        this.teacherId = teacherId;
        this.level = level;
        this.category = category;
        this.duration = duration;
        this.createdAt = new Date();
        this.updatedAt = new Date();
        this.isActive = true;
        this.enrolledStudents = 0;
        this.rating = 0.0;
    }

    // Getters and Setters
    public String getId() { return id; }
    public void setId(String id) { this.id = id; }

    public String getTitle() { return title; }
    public void setTitle(String title) { this.title = title; }

    public String getDescription() { return description; }
    public void setDescription(String description) { this.description = description; }

    public String getTeacherId() { return teacherId; }
    public void setTeacherId(String teacherId) { this.teacherId = teacherId; }

    public String getImageUrl() { return imageUrl; }
    public void setImageUrl(String imageUrl) { this.imageUrl = imageUrl; }

    public String getLevel() { return level; }
    public void setLevel(String level) { this.level = level; }

    public String getCategory() { return category; }
    public void setCategory(String category) { this.category = category; }

    public int getDuration() { return duration; }
    public void setDuration(int duration) { this.duration = duration; }

    public Date getCreatedAt() { return createdAt; }
    public void setCreatedAt(Date createdAt) { this.createdAt = createdAt; }

    public Date getUpdatedAt() { return updatedAt; }
    public void setUpdatedAt(Date updatedAt) { this.updatedAt = updatedAt; }

    public boolean isActive() { return isActive; }
    public void setActive(boolean active) { isActive = active; }

    public int getEnrolledStudents() { return enrolledStudents; }
    public void setEnrolledStudents(int enrolledStudents) { this.enrolledStudents = enrolledStudents; }

    public double getRating() { return rating; }
    public void setRating(double rating) { this.rating = rating; }

    public List<String> getTags() { return tags; }
    public void setTags(List<String> tags) { this.tags = tags; }

    // Thêm phương thức getCourseName() để tương thích với code hiện tại
    public String getCourseName() {
        return this.title;
    }

    public void setCourseName(String courseName) {
        this.title = courseName;
    }
}

package com.vantinh.tienganh.models;

import java.util.Date;

public class LessonProgress {
    private String id;
    private String studentId;
    private String courseId;
    private String lessonId;
    private boolean isCompleted;
    private Date completedAt;
    private Date createdAt;
    private Date updatedAt;

    public LessonProgress() {
        // Constructor mặc định cho Firestore
    }

    public LessonProgress(String studentId, String courseId, String lessonId, boolean isCompleted) {
        this.studentId = studentId;
        this.courseId = courseId;
        this.lessonId = lessonId;
        this.isCompleted = isCompleted;
        this.createdAt = new Date();
        this.updatedAt = new Date();
        if (isCompleted) {
            this.completedAt = new Date();
        }
    }

    // Getters and Setters
    public String getId() {
        return id;
    }

    public void setId(String id) {
        this.id = id;
    }

    public String getStudentId() {
        return studentId;
    }

    public void setStudentId(String studentId) {
        this.studentId = studentId;
    }

    public String getCourseId() {
        return courseId;
    }

    public void setCourseId(String courseId) {
        this.courseId = courseId;
    }

    public String getLessonId() {
        return lessonId;
    }

    public void setLessonId(String lessonId) {
        this.lessonId = lessonId;
    }

    public boolean isCompleted() {
        return isCompleted;
    }

    public void setCompleted(boolean completed) {
        isCompleted = completed;
        this.updatedAt = new Date();
        if (completed && this.completedAt == null) {
            this.completedAt = new Date();
        }
    }

    public Date getCompletedAt() {
        return completedAt;
    }

    public void setCompletedAt(Date completedAt) {
        this.completedAt = completedAt;
    }

    public Date getCreatedAt() {
        return createdAt;
    }

    public void setCreatedAt(Date createdAt) {
        this.createdAt = createdAt;
    }

    public Date getUpdatedAt() {
        return updatedAt;
    }

    public void setUpdatedAt(Date updatedAt) {
        this.updatedAt = updatedAt;
    }
}

